import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { useAuth } from '../contexts/AuthContext';
import apiClient from '../services/apiClient';
import './SocialMediaPage.css';

function FacebookPage() {
  const navigate = useNavigate();
  const { user, isAuthenticated: authIsAuthenticated, logout } = useAuth();
  const [isConnecting, setIsConnecting] = useState(false);
  const [connectionStatus, setConnectionStatus] = useState('');
  
  // Facebook OAuth states
  const [pageAccessToken, setPageAccessToken] = useState('');
  const [selectedPage, setSelectedPage] = useState(null);
  const [facebookConnected, setFacebookConnected] = useState(false);
  
  // Auto-reply toggle state
  const [autoReplyEnabled, setAutoReplyEnabled] = useState(false);
  
  // Tab selection state
  const [activeTab, setActiveTab] = useState('auto');
  
  // Auto post states
  const [autoFormData, setAutoFormData] = useState({
    prompt: '',
    mediaType: 'none', // 'none', 'photo', 'video'
    mediaFile: null,
    generatedContent: '',
    isGenerating: false
  });
  
  // Manual post states
  const [manualFormData, setManualFormData] = useState({
    message: '',
    mediaType: 'none',
    mediaFile: null
  });

  // Icon Components
  const MessageIcon = () => (
    <svg width="16" height="16" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2">
      <path d="M21 15a2 2 0 0 1-2 2H7l-4 4V5a2 2 0 0 1 2-2h14a2 2 0 0 1 2 2z"></path>
    </svg>
  );

  const PageIcon = () => (
    <svg width="16" height="16" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2">
      <path d="M13 2L3 14h9l-1 8 10-12h-9l1-8z"></path>
    </svg>
  );

  const ImageIcon = () => (
    <svg width="16" height="16" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2">
      <rect x="3" y="3" width="18" height="18" rx="2" ry="2"></rect>
      <circle cx="8.5" cy="8.5" r="1.5"></circle>
      <polyline points="21,15 16,10 5,21"></polyline>
    </svg>
  );

  const AutoReplyIcon = () => (
    <svg width="16" height="16" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2">
      <path d="M21 11.5a8.38 8.38 0 0 1-.9 3.8 8.5 8.5 0 0 1-7.6 4.7 8.38 8.38 0 0 1-3.8-.9L3 21l1.9-5.7a8.38 8.38 0 0 1-.9-3.8 8.5 8.5 0 0 1 4.7-7.6 8.38 8.38 0 0 1 3.8-.9h.5a8.48 8.48 0 0 1 8 8v.5z"></path>
    </svg>
  );

  const LockIcon = () => (
    <svg width="16" height="16" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2">
      <rect x="3" y="11" width="18" height="11" rx="2" ry="2"></rect>
      <circle cx="12" cy="16" r="1"></circle>
      <path d="M7 11V7a5 5 0 0 1 10 0v4"></path>
    </svg>
  );

  const RocketIcon = () => (
    <svg width="16" height="16" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2">
      <path d="M4.5 16.5c-1.5 1.26-2 5-2 5s3.74-.5 5-2c.71-.84.7-2.13-.09-2.91a2.18 2.18 0 0 0-2.91-.09z"></path>
      <path d="M12 15l-3-3a22 22 0 0 1 2-3.95A12.88 12.88 0 0 1 22 2c0 2.72-.78 7.5-6 11a22.35 22.35 0 0 1-4 2z"></path>
      <path d="M9 12H4s.55-3.03 2-4c1.62-1.08 5 0 5 0"></path>
      <path d="M12 15v5s3.03-.55 4-2c1.08-1.62 0-5 0-5"></path>
    </svg>
  );

  const LogoutIcon = () => (
    <svg width="16" height="16" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2">
      <path d="M9 21H5a2 2 0 0 1-2-2V5a2 2 0 0 1 2-2h4"></path>
      <polyline points="16,17 21,12 16,7"></polyline>
      <line x1="21" y1="12" x2="9" y2="12"></line>
    </svg>
  );

  const AutoIcon = () => (
    <svg width="16" height="16" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2">
      <path d="M12 2L2 7l10 5 10-5-10-5zM2 17l10 5 10-5M2 12l10 5 10-5"/>
    </svg>
  );

  const ManualIcon = () => (
    <svg width="16" height="16" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2">
      <path d="M17 3a2.85 2.83 0 1 1 4 4L7.5 20.5 2 22l1.5-5.5L17 3z"/>
    </svg>
  );

  const VideoIcon = () => (
    <svg width="16" height="16" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2">
      <polygon points="23 7 16 12 23 17 23 7"></polygon>
      <rect x="1" y="5" width="15" height="14" rx="2" ry="2"></rect>
    </svg>
  );

  const SparkleIcon = () => (
    <svg width="16" height="16" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2">
      <path d="M9.937 15.5A2 2 0 0 0 8.5 14.063l-6.135-1.582a.5.5 0 0 1 0-.962L8.5 9.936A2 2 0 0 0 9.937 8.5l1.582-6.135a.5.5 0 0 1 .963 0L14.063 8.5A2 2 0 0 0 15.5 9.937l6.135 1.582a.5.5 0 0 1 0 .962L15.5 14.063a2 2 0 0 0-1.437 1.437l-1.582 6.135a.5.5 0 0 1-.963 0L9.937 15.5Z"/>
    </svg>
  );

  // Clean up any existing Facebook SDK instances
  const cleanupFacebookSDK = () => {
    const existingScript = document.getElementById('facebook-jssdk');
    if (existingScript) {
      existingScript.remove();
    }
    
    if (window.FB) {
      delete window.FB;
    }
    
    if (window.fbAsyncInit) {
      delete window.fbAsyncInit;
    }
  };

  // Load Facebook SDK function
  const loadFacebookSDK = () => {
    return new Promise((resolve, reject) => {
      cleanupFacebookSDK();
      
      window.fbAsyncInit = function () {
        try {
          const appId = process.env.REACT_APP_FACEBOOK_APP_ID || '1526961221410200';
          
          window.FB.init({
            appId: appId,
            cookie: true,
            xfbml: true,
            version: 'v19.0'
          });

          // Remove the automatic login status check since we'll handle it explicitly
          resolve();
        } catch (error) {
          console.error('Facebook SDK initialization error:', error);
          reject(error);
        }
      };

      const script = document.createElement('script');
      script.id = 'facebook-jssdk';
      script.src = 'https://connect.facebook.net/en_US/sdk.js';
      script.async = true;
      script.defer = true;
      script.crossOrigin = 'anonymous';

      script.onerror = () => {
        reject(new Error('Failed to load Facebook SDK script'));
      };

      document.body.appendChild(script);
    });
  };

  const handleAutoInputChange = (e) => {
    const { name, value, files } = e.target;
    setAutoFormData(prev => ({
      ...prev,
      [name]: files ? files[0] : value
    }));
  };

  const handleManualInputChange = (e) => {
    const { name, value, files } = e.target;
    setManualFormData(prev => ({
      ...prev,
      [name]: files ? files[0] : value
    }));
  };

  const handleMediaTypeChange = (type, formType) => {
    if (formType === 'auto') {
      setAutoFormData(prev => ({
        ...prev,
        mediaType: type,
        mediaFile: null
      }));
    } else {
      setManualFormData(prev => ({
        ...prev,
        mediaType: type,
        mediaFile: null
      }));
    }
  };

  // Generate content using Groq API
  const generatePostContent = async () => {
    if (!autoFormData.prompt.trim()) {
      setConnectionStatus('âŒ Please enter a prompt for AI generation');
      return;
    }

    setAutoFormData(prev => ({ ...prev, isGenerating: true }));
    setConnectionStatus('ðŸ¤– Generating content with AI...');

    try {
      // Call your backend API to use Groq
      const response = await apiClient.generateContent(autoFormData.prompt);
      
      setAutoFormData(prev => ({
        ...prev,
        generatedContent: response.content,
        isGenerating: false
      }));
      
      setConnectionStatus('âœ… Content generated successfully!');
    } catch (error) {
      console.error('Content generation error:', error);
      setConnectionStatus('âŒ Failed to generate content: ' + (error.message || 'Unknown error'));
      setAutoFormData(prev => ({ ...prev, isGenerating: false }));
    }
  };

  // UPDATED: Replace Make.com auto-reply webhook with backend API
  const handleAutoReplyToggle = async () => {
    const newState = !autoReplyEnabled;
    setAutoReplyEnabled(newState);
    
    try {
      setConnectionStatus('ðŸ”„ Updating auto-reply setting...');
      
      await apiClient.toggleAutoReply(
        selectedPage?.id || 'user_profile',
        newState,
        'Thank you for your comment! We\'ll get back to you soon.'
      );

      setConnectionStatus(`${newState ? 'âœ…' : 'âŒ'} Auto-reply ${newState ? 'enabled' : 'disabled'}`);
    } catch (error) {
      console.error('Auto-reply toggle error:', error);
      setConnectionStatus('âŒ Failed to update auto-reply setting');
      setAutoReplyEnabled(!newState); // Revert on error
    }
  };

  const loginWithFacebook = async () => {
    // Check HTTPS requirement first
    if (window.location.protocol !== 'https:' && window.location.hostname !== 'localhost') {
      setConnectionStatus('âŒ Facebook login requires HTTPS. Please use https://localhost:3001 or deploy with HTTPS');
      return;
    }

    // Check if user is still authenticated with our backend
    try {
      await apiClient.getCurrentUser();
    } catch (error) {
      setConnectionStatus('âŒ Your session has expired. Please log out and log back in to connect Facebook.');
      setTimeout(() => {
        logout();
        navigate('/');
      }, 3000);
      return;
    }

    setIsConnecting(true);
    setConnectionStatus('ðŸ“¦ Loading Facebook SDK...');

    try {
      await loadFacebookSDK();

      if (!window.FB || typeof window.FB.login !== 'function') {
        setConnectionStatus('âŒ Facebook SDK failed to load. Please refresh the page and try again.');
        setIsConnecting(false);
        return;
      }

      setConnectionStatus('ðŸ” Connecting to Facebook...');
      
      window.FB.login((response) => {
        if (response.authResponse) {
          setConnectionStatus('âœ… Facebook login successful!');
          // First fetch pages to ensure we have access
          fetchPages(response.authResponse.accessToken).then(() => {
            connectToBackend(response.authResponse);
          });
        } else if (response.status === 'not_authorized') {
          setConnectionStatus('âŒ Please authorize the app to continue and select at least one page.');
          setIsConnecting(false);
        } else {
          setConnectionStatus('âŒ Facebook login cancelled or failed');
          setIsConnecting(false);
        }
      }, {
        scope: [
          'public_profile',
          'pages_show_list',
          'pages_read_engagement', 
          'pages_manage_posts',
          'pages_manage_engagement',
          'pages_read_user_content'
        ].join(','),
        enable_profile_selector: true,
        return_scopes: true
      });
    } catch (error) {
      console.error('Facebook login error:', error);
      setConnectionStatus('âŒ Facebook login failed: ' + error.message);
      setIsConnecting(false);
    }
  };

  // UPDATED: Connect Facebook to backend instead of just setting local state
  const connectToBackend = async (authResponse) => {
    try {
      setConnectionStatus('ðŸ”— Connecting to backend...');
      
      // Get user info from Facebook
      const userInfo = await new Promise((resolve, reject) => {
        window.FB.api('/me', { 
          access_token: authResponse.accessToken,
          fields: 'id,name,email' 
        }, (response) => {
          if (response.error) reject(new Error(response.error.message));
          else resolve(response);
        });
      });

      // Get user's pages with extended permissions
      const pagesResponse = await new Promise((resolve, reject) => {
        window.FB.api('/me/accounts', { 
          access_token: authResponse.accessToken,
          fields: 'id,name,category,access_token,picture,fan_count,tasks'
        }, (response) => {
          if (response.error) reject(new Error(response.error.message));
          else resolve(response);
        });
      });

      console.log('Facebook pages response:', pagesResponse);
      const pages = pagesResponse.data || [];
      console.log('Facebook pages data:', pages);
      
      try {
        // Map pages with required fields and permissions
        const mappedPages = pages.map(page => ({
          id: page.id,
          name: page.name,
          category: page.category || 'Page',
          access_token: page.access_token,
          can_post: page.tasks?.includes('CREATE_CONTENT') || page.tasks?.includes('MANAGE_CONTENT') || true
        }));

        console.log('Mapped pages for backend:', mappedPages);

        // Connect to our backend
        const result = await apiClient.connectFacebook(
          authResponse.accessToken,
          userInfo.id,
          mappedPages
        );

        console.log('Backend connection result:', result);

        // Set the first page as selected if available
        if (pages.length > 0) {
          const firstPage = pages[0];
          setSelectedPage({
            id: firstPage.id,
            name: firstPage.name,
            access_token: firstPage.access_token,
            category: firstPage.category || 'Page',
            profilePicture: firstPage.picture?.data?.url || '',
            isUserProfile: false,
            canPost: true,
            canComment: true,
            followerCount: firstPage.fan_count || 0
          });
          setPageAccessToken(firstPage.access_token);
        } else {
          // Fallback to user profile if no pages available
          setSelectedPage({
            id: userInfo.id,
            name: userInfo.name,
            access_token: authResponse.accessToken,
            category: 'Personal Profile',
            profilePicture: '',
            email: userInfo.email || '',
            isUserProfile: true,
            canPost: true,
            canComment: true,
            followerCount: 0
          });
          setPageAccessToken(authResponse.accessToken);
        }

        setFacebookConnected(true);
        setConnectionStatus(`âœ… Connected successfully! ${pages.length} pages found.`);
        setIsConnecting(false);
      } catch (backendError) {
        console.error('Backend connection error:', backendError);
        
        if (backendError.message.includes('Could not validate credentials') || 
            backendError.message.includes('401') ||
            backendError.message.includes('Unauthorized')) {
          setConnectionStatus('âŒ Your session has expired. Please log out and log back in to connect Facebook.');
          setTimeout(() => {
            logout();
            navigate('/');
          }, 3000);
        } else {
          setConnectionStatus('âŒ Failed to connect to backend: ' + (backendError.message || 'Unknown error'));
        }
        
        setIsConnecting(false);
        setFacebookConnected(false);
      }
    } catch (error) {
      console.error('Facebook API error:', error);
      setConnectionStatus('âŒ Failed to get Facebook data: ' + (error.message || 'Unknown error'));
      setIsConnecting(false);
      setFacebookConnected(false);
    }
  };

  const fetchPages = async (userAccessToken) => {
    setConnectionStatus('ðŸ“„ Setting up your Facebook connection...');
    
    try {
      // Get user profile info without setting global access token
      const userResponse = await new Promise((resolve) => {
        window.FB.api('/me', {
          access_token: userAccessToken,
          fields: 'id,name,picture,email'
        }, resolve);
      });

      if (userResponse.error) {
        console.error('Facebook API error:', userResponse.error);
        setConnectionStatus(`âŒ Facebook API error: ${userResponse.error.message}`);
        setIsConnecting(false);
        return;
      }

      // Get pages with the user access token
      const pagesResponse = await new Promise((resolve) => {
        window.FB.api('/me/accounts', {
          access_token: userAccessToken,
          fields: 'id,name,category,access_token,picture,fan_count,tasks'
        }, resolve);
      });

      console.log('Pages response:', pagesResponse);

      if (pagesResponse.error) {
        console.error('Facebook Pages API error:', pagesResponse.error);
        setConnectionStatus(`âŒ Facebook Pages API error: ${pagesResponse.error.message}`);
        setIsConnecting(false);
        return;
      }

      const pages = pagesResponse.data || [];
      
      if (pages.length === 0) {
        console.log('No pages found. User might need to select pages during login.');
        // We'll handle this case in the login flow
        setIsConnecting(false);
        return;
      }

      setConnectionStatus('âœ… Facebook connected successfully!');
      setFacebookConnected(true);
      
      // Set up user profile for posting
      setSelectedPage({
        id: userResponse.id,
        name: userResponse.name,
        access_token: userAccessToken,
        category: 'Personal Profile',
        profilePicture: userResponse.picture?.data?.url || '',
        email: userResponse.email || '',
        isUserProfile: true,
        canPost: true,
        canComment: true,
        followerCount: 0
      });
      setPageAccessToken(userAccessToken);
      setIsConnecting(false);
    } catch (error) {
      console.error('Fetch user profile error:', error);
      setConnectionStatus('âŒ Failed to connect to Facebook');
      setIsConnecting(false);
    }
  };

  // Handle auto post submission
  const handleAutoPost = async () => {
    if (!facebookConnected) {
      loginWithFacebook();
      return;
    }

    const content = autoFormData.generatedContent || autoFormData.prompt;
    if (!content || content.trim() === '') {
      setConnectionStatus('âŒ Please generate content or enter a prompt');
      return;
    }

    if (!pageAccessToken) {
      setConnectionStatus('âŒ No Facebook access token available');
      return;
    }

    if (autoFormData.mediaType !== 'none' && !autoFormData.mediaFile) {
      setConnectionStatus(`âŒ Please select a ${autoFormData.mediaType} file`);
      return;
    }

    try {
      setIsConnecting(true);
      setConnectionStatus('ðŸš€ Posting to Facebook...');
      
      const result = await apiClient.createFacebookPost(
        selectedPage?.id || 'user_profile',
        content,
        'auto-generated',
        autoFormData.mediaFile ? autoFormData.mediaFile.name : null
      );

      setConnectionStatus(`âœ… ${result.message}`);
      
      // Reset auto form
      setAutoFormData({
        prompt: '',
        mediaType: 'none',
        mediaFile: null,
        generatedContent: '',
        isGenerating: false
      });
    } catch (error) {
      console.error('Auto post creation error:', error);
      handlePostError(error);
    } finally {
      setIsConnecting(false);
    }
  };

  // Handle manual post submission
  const handleManualPost = async () => {
    if (!facebookConnected) {
      loginWithFacebook();
      return;
    }

    if (!manualFormData.message || manualFormData.message.trim() === '') {
      setConnectionStatus('âŒ Please enter a message');
      return;
    }

    if (!pageAccessToken) {
      setConnectionStatus('âŒ No Facebook access token available');
      return;
    }

    if (manualFormData.mediaType !== 'none' && !manualFormData.mediaFile) {
      setConnectionStatus(`âŒ Please select a ${manualFormData.mediaType} file`);
      return;
    }

    try {
      setIsConnecting(true);
      setConnectionStatus('ðŸš€ Posting to Facebook...');
      
      const result = await apiClient.createFacebookPost(
        selectedPage?.id || 'user_profile',
        manualFormData.message,
        'manual',
        manualFormData.mediaFile ? manualFormData.mediaFile.name : null
      );

      setConnectionStatus(`âœ… ${result.message}`);
      
      // Reset manual form
      setManualFormData({
        message: '',
        mediaType: 'none',
        mediaFile: null
      });
    } catch (error) {
      console.error('Manual post creation error:', error);
      handlePostError(error);
    } finally {
      setIsConnecting(false);
    }
  };

  // Common error handler for posts
  const handlePostError = (error) => {
    if (error.message.includes('Could not validate credentials') || 
        error.message.includes('401') ||
        error.message.includes('Unauthorized')) {
      setConnectionStatus('âŒ Your session has expired. Please log out and log back in to post.');
      setTimeout(() => {
        logout();
        navigate('/');
      }, 3000);
    } else {
      setConnectionStatus('âŒ Failed to create post: ' + (error.message || 'Unknown error'));
    }
  };

  // Logout from Facebook only (not the entire app)
  const handleLogout = async () => {
    if (window.FB) {
      window.FB.logout(() => {
        // Facebook logout complete
      });
    }
    
    // Reset Facebook-related state but keep user logged into the app
    setFacebookConnected(false);
    setSelectedPage(null);
    setPageAccessToken('');
    setAutoReplyEnabled(false);
    setConnectionStatus('ðŸ‘‹ Logged out from Facebook successfully');
    
    // Don't navigate away - stay on the Facebook page
    // navigate('/social-media');
  };

  // Show login form if not authenticated with our system
  if (!authIsAuthenticated) {
    return (
      <div className="page-container">
        <div className="auth-required">
          <h1>Please login to continue</h1>
          <p>You need to be logged in to use Facebook automation features.</p>
          <button onClick={() => navigate('/')} className="btn primary">
            Go to Login
          </button>
        </div>
      </div>
    );
  }

  return (
    <div className="page-container">
      <header className="page-header">
        <button onClick={() => navigate('/')} className="back-btn">
          <svg width="20" height="20" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2">
            <polyline points="15,18 9,12 15,6"></polyline>
          </svg>
          Back to Social Media
        </button>
        <div className="page-title-section">
          <h1 className="page-title">Facebook Automation</h1>
          <p className="page-subtitle">Connected to FastAPI Backend â€¢ User: {user?.full_name || user?.username}</p>
        </div>
      </header>

      <main className="page-main">
        <div className="platforms-container">
          <div className="platforms-grid">
            <div className={`platform-card ${facebookConnected ? 'authenticated' : ''}`}
                 style={{
                   '--platform-gradient': 'linear-gradient(135deg, #1877F2 0%, #42a5f5 100%)',
                   '--platform-border': '#1877F2'
                 }}>
              <div className="platform-icon">
                <svg width="64" height="64" viewBox="0 0 24 24" fill="#1877F2">
                  <path d="M24 12.073c0-6.627-5.373-12-12-12s-12 5.373-12 12c0 5.99 4.388 10.954 10.125 11.854v-8.385H7.078v-3.47h3.047V9.43c0-3.007 1.792-4.669 4.533-4.669 1.312 0 2.686.235 2.686.235v2.953H15.83c-1.491 0-1.956.925-1.956 1.874v2.25h3.328l-.532 3.47h-2.796v8.385C19.612 23.027 24 18.062 24 12.073z"/>
                </svg>
              </div>
              
              <div className="platform-content">
                <h3 className="platform-name">Facebook</h3>
                <p className="platform-description">
                  {facebookConnected ? `Connected as ${selectedPage?.name || 'Facebook User'}` : 'Not Connected'}
                </p>
                
                {facebookConnected ? (
                  <div className="platform-form">
                    {/* Auto-reply toggle - Always visible */}
                    <div className="form-group auto-reply-group">
                      <label className="form-label toggle-label">
                        <AutoReplyIcon />
                        Auto-reply to comments
                        <div className="toggle-switch">
                          <input
                            type="checkbox"
                            checked={autoReplyEnabled}
                            onChange={handleAutoReplyToggle}
                            className="toggle-input"
                          />
                          <span className="toggle-slider"></span>
                        </div>
                      </label>
                    </div>

                    {/* Connection Info */}
                    <div className="form-group">
                      <label className="form-label">
                        <PageIcon />
                        Connected Account
                      </label>
                      {selectedPage?.isUserProfile ? (
                        <div className="page-card selected">
                          <div className="page-avatar">
                            {selectedPage.profilePicture ? (
                              <img src={selectedPage.profilePicture} alt={selectedPage.name} />
                            ) : (
                              <div className="page-avatar-placeholder">
                                <svg width="16" height="16" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2">
                                  <path d="M20 21v-2a4 4 0 0 0-4-4H8a4 4 0 0 0-4 4v2"></path>
                                  <circle cx="12" cy="7" r="4"></circle>
                                </svg>
                              </div>
                            )}
                          </div>
                          <div className="page-info">
                            <div className="page-details">
                            </div>
                            <div className="page-permissions">
                              <span className="permission-badge post">{selectedPage.name}</span>
                            </div>
                          </div>
                          <div className="page-selector">
                            <div className="radio-button selected">
                              <svg width="12" height="12" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="3">
                                <polyline points="20,6 9,17 4,12"></polyline>
                              </svg>
                            </div>
                          </div>
                        </div>
                      ) : (
                        <div className="no-pages-message">
                          <svg width="24" height="24" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2">
                            <circle cx="12" cy="12" r="10"></circle>
                            <line x1="12" y1="8" x2="12" y2="12"></line>
                            <line x1="12" y1="16" x2="12.01" y2="16"></line>
                          </svg>
                          <p>No pages found. Please make sure you have Facebook pages associated with your account.</p>
                        </div>
                      )}
                    </div>
                    
                    {/* Tab Navigation */}
                    <div className="tab-navigation">
                      <div className="tab-buttons">
                        <button 
                          className={`tab-button ${activeTab === 'auto' ? 'active' : ''}`}
                          onClick={() => setActiveTab('auto')}
                        >
                          <AutoIcon />
                          Auto Post
                          <SparkleIcon />
                        </button>
                        <button 
                          className={`tab-button ${activeTab === 'manual' ? 'active' : ''}`}
                          onClick={() => setActiveTab('manual')}
                        >
                          <ManualIcon />
                          Manual Post
                        </button>
                      </div>
                    </div>

                    {/* Auto Post Form */}
                    {activeTab === 'auto' && (
                      <div className="tab-content auto-tab">
                        <div className="form-group">
                          <label className="form-label">
                            <SparkleIcon />
                            AI Prompt
                          </label>
                          <div className="prompt-container">
                            <textarea
                              name="prompt"
                              placeholder="Describe what you want to post about... e.g., 'AI in the future'"
                              value={autoFormData.prompt}
                              onChange={handleAutoInputChange}
                              className="glass-input glass-textarea prompt-textarea"
                              rows="3"
                            />
                            <button 
                              className="generate-btn"
                              onClick={generatePostContent}
                              disabled={autoFormData.isGenerating || !autoFormData.prompt.trim()}
                            >
                              {autoFormData.isGenerating ? (
                                <>
                                  <div className="loading-spinner" />
                                  Generating...
                                </>
                              ) : (
                                <>
                                  <SparkleIcon />
                                  Generate
                                </>
                              )}
                            </button>
                          </div>
                        </div>

                        {autoFormData.generatedContent && (
                          <div className="form-group">
                            <label className="form-label">
                              <MessageIcon />
                              Generated Content
                            </label>
                            <textarea
                              name="generatedContent"
                              value={autoFormData.generatedContent}
                              onChange={handleAutoInputChange}
                              className="glass-input glass-textarea generated-content"
                              rows="4"
                              placeholder="Generated content will appear here..."
                            />
                          </div>
                        )}

                        {/* Media Type Selection for Auto */}
                        <div className="form-group">
                          <label className="form-label">
                            <ImageIcon />
                            Media Type
                          </label>
                          <div className="media-type-selector">
                            <button 
                              className={`media-type-btn ${autoFormData.mediaType === 'none' ? 'active' : ''}`}
                              onClick={() => handleMediaTypeChange('none', 'auto')}
                            >
                              <MessageIcon />
                              Text Only
                            </button>
                            <button 
                              className={`media-type-btn ${autoFormData.mediaType === 'photo' ? 'active' : ''}`}
                              onClick={() => handleMediaTypeChange('photo', 'auto')}
                            >
                              <ImageIcon />
                              Photo
                            </button>
                            <button 
                              className={`media-type-btn ${autoFormData.mediaType === 'video' ? 'active' : ''}`}
                              onClick={() => handleMediaTypeChange('video', 'auto')}
                            >
                              <VideoIcon />
                              Video
                            </button>
                          </div>
                        </div>

                        {/* Media Upload for Auto */}
                        {autoFormData.mediaType !== 'none' && (
                          <div className="form-group">
                            <label className="form-label">
                              {autoFormData.mediaType === 'photo' ? <ImageIcon /> : <VideoIcon />}
                              Upload {autoFormData.mediaType === 'photo' ? 'Photo' : 'Video'}
                            </label>
                            <div className="media-upload-wrapper">
                              <input
                                type="file"
                                name="mediaFile"
                                accept={autoFormData.mediaType === 'photo' ? 'image/*' : 'video/*'}
                                onChange={handleAutoInputChange}
                                className="media-input"
                                id="auto-media-upload"
                              />
                              <label htmlFor="auto-media-upload" className="media-upload-label">
                                {autoFormData.mediaType === 'photo' ? <ImageIcon /> : <VideoIcon />}
                                {autoFormData.mediaFile ? autoFormData.mediaFile.name : `Choose a ${autoFormData.mediaType}`}
                              </label>
                            </div>
                          </div>
                        )}
                      </div>
                    )}

                    {/* Manual Post Form */}
                    {activeTab === 'manual' && (
                      <div className="tab-content manual-tab">
                        <div className="form-group">
                          <label className="form-label">
                            <MessageIcon />
                            Your Message
                          </label>
                          <textarea
                            name="message"
                            placeholder="What's on your mind? Share your thoughts with the world..."
                            value={manualFormData.message}
                            onChange={handleManualInputChange}
                            className="glass-input glass-textarea enhanced-textarea"
                            rows="5"
                          />
                        </div>

                        {/* Media Type Selection for Manual */}
                        <div className="form-group">
                          <label className="form-label">
                            <ImageIcon />
                            Media Type
                          </label>
                          <div className="media-type-selector">
                            <button 
                              className={`media-type-btn ${manualFormData.mediaType === 'none' ? 'active' : ''}`}
                              onClick={() => handleMediaTypeChange('none', 'manual')}
                            >
                              <MessageIcon />
                              Text Only
                            </button>
                            <button 
                              className={`media-type-btn ${manualFormData.mediaType === 'photo' ? 'active' : ''}`}
                              onClick={() => handleMediaTypeChange('photo', 'manual')}
                            >
                              <ImageIcon />
                              Photo
                            </button>
                            <button 
                              className={`media-type-btn ${manualFormData.mediaType === 'video' ? 'active' : ''}`}
                              onClick={() => handleMediaTypeChange('video', 'manual')}
                            >
                              <VideoIcon />
                              Video
                            </button>
                          </div>
                        </div>

                        {/* Media Upload for Manual */}
                        {manualFormData.mediaType !== 'none' && (
                          <div className="form-group">
                            <label className="form-label">
                              {manualFormData.mediaType === 'photo' ? <ImageIcon /> : <VideoIcon />}
                              Upload {manualFormData.mediaType === 'photo' ? 'Photo' : 'Video'}
                            </label>
                            <div className="media-upload-wrapper">
                              <input
                                type="file"
                                name="mediaFile"
                                accept={manualFormData.mediaType === 'photo' ? 'image/*' : 'video/*'}
                                onChange={handleManualInputChange}
                                className="media-input"
                                id="manual-media-upload"
                              />
                              <label htmlFor="manual-media-upload" className="media-upload-label">
                                {manualFormData.mediaType === 'photo' ? <ImageIcon /> : <VideoIcon />}
                                {manualFormData.mediaFile ? manualFormData.mediaFile.name : `Choose a ${manualFormData.mediaType}`}
                              </label>
                            </div>
                          </div>
                        )}
                      </div>
                    )}
                  </div>
                ) : (
                  <div className="auth-prompt">
                    <p className="auth-description">
                      <LockIcon />
                      Please authenticate with Facebook to start posting
                    </p>
                  </div>
                )}
              </div>

              <div className="platform-actions">
                {facebookConnected ? (
                  <div className="auth-actions">
                    <button 
                      className="connect-btn"
                      onClick={activeTab === 'auto' ? handleAutoPost : handleManualPost}
                      disabled={isConnecting}
                    >
                      {isConnecting ? (
                        <>
                          <div className="loading-spinner" />
                          Posting...
                        </>
                      ) : (
                        <>
                          <RocketIcon />
                          {activeTab === 'auto' ? 'Post with AI' : 'Post to Facebook'}
                          {activeTab === 'auto' && <SparkleIcon />}
                          <svg width="16" height="16" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2">
                            <polyline points="9,18 15,12 9,6"></polyline>
                          </svg>
                        </>
                      )}
                    </button>
                    <button 
                      className="logout-btn"
                      onClick={handleLogout}
                    >
                      <LogoutIcon />
                      Logout
                    </button>
                  </div>
                ) : (
                  <button 
                    className="connect-btn"
                    onClick={loginWithFacebook}
                    disabled={isConnecting}
                  >
                    {isConnecting ? (
                      <>
                        <div className="loading-spinner" />
                        Connecting...
                      </>
                    ) : (
                      <>
                        <LockIcon />
                        Connect Facebook
                        <svg width="16" height="16" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2">
                          <polyline points="9,18 15,12 9,6"></polyline>
                        </svg>
                      </>
                    )}
                  </button>
                )}
              </div>
            </div>
          </div>
          
          {connectionStatus && (
            <div className="connection-status">
              <div className={`status-message ${connectionStatus.includes('âœ…') ? 'success' : connectionStatus.includes('ðŸš§') ? 'info' : 'error'}`}>
                {connectionStatus}
              </div>
            </div>
          )}
        </div>

        {/* Backend Status Indicator */}
        <div className="backend-status-footer">
          <div className="backend-indicator">
            <span className="status-dot"></span>
            <span>Connected to FastAPI Backend</span>
          </div>
        </div>
      </main>
    </div>
  );
}

export default FacebookPage;
