import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { useAuth } from '../contexts/AuthContext';
import apiClient from '../services/apiClient';
import facebookSDK from '../services/facebookSDK';
import './SocialMediaPage.css';

function InstagramPage() {
  const navigate = useNavigate();
  const { logout } = useAuth();
  const [isConnecting, setIsConnecting] = useState(false);
  const [connectionStatus, setConnectionStatus] = useState('');
  const [selectedAccount, setSelectedAccount] = useState(null);
  const [availableAccounts, setAvailableAccounts] = useState([]);
  const [instagramConnected, setInstagramConnected] = useState(false);
  const [activeTab, setActiveTab] = useState('auto');
  const [cardFlipped, setCardFlipped] = useState(false);
  const [isAlreadyLoggedIn, setIsAlreadyLoggedIn] = useState(false);
  
  // Auto post states
  const [autoFormData, setAutoFormData] = useState({
    prompt: '',
    imageUrl: '',
    generatedContent: '',
    isGenerating: false
  });
  
  // Manual post states
  const [manualFormData, setManualFormData] = useState({
    caption: '',
    imageUrl: ''
  });

  // Publishing state
  const [isPublishing, setIsPublishing] = useState(false);

  // Check if user is already logged in to Facebook on component mount
  useEffect(() => {
    checkExistingFacebookLogin();
  }, []);

  const checkExistingFacebookLogin = async () => {
    try {
      setConnectionStatus('ðŸ” Checking existing Facebook connection...');
      
      const isLoggedIn = await facebookSDK.isLoggedIn();
      if (isLoggedIn) {
        const accessToken = facebookSDK.getCurrentAccessToken();
        if (accessToken) {
          setIsAlreadyLoggedIn(true);
          setConnectionStatus('âœ… Found existing Facebook login! Click below to connect Instagram accounts.');
        } else {
          setConnectionStatus('');
        }
      } else {
        setConnectionStatus('');
      }
    } catch (error) {
      console.error('Error checking existing login:', error);
      setConnectionStatus('');
    }
  };

  // Icon Components
  const MediaIcon = ({ type }) => {
    switch(type) {
      case 'photo':
        return (
          <svg width="24" height="24" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2">
            <rect x="3" y="3" width="18" height="18" rx="2" ry="2"/>
            <circle cx="8.5" cy="8.5" r="1.5"/>
            <polyline points="21,15 16,10 5,21"/>
          </svg>
        );
      default:
        return (
          <svg width="24" height="24" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2">
            <path d="M21 15a2 2 0 0 1-2 2H7l-4 4V5a2 2 0 0 1 2-2h14a2 2 0 0 1 2 2z"/>
          </svg>
        );
    }
  };

  // Fetch Instagram accounts function using shared SDK
  const fetchInstagramAccounts = async (accessToken) => {
    if (!accessToken) {
      setConnectionStatus('âŒ No Facebook access token found. Please try reconnecting.');
      setIsConnecting(false);
      setInstagramConnected(false);
      return { mappedAccounts: [], userInfo: null };
    }
    
    try {
      // Check permissions using shared SDK
      const permissionsResponse = await facebookSDK.api('/me/permissions', { access_token: accessToken });
      
      const grantedPermissions = permissionsResponse.data?.filter(p => p.status === 'granted').map(p => p.permission) || [];
      const requiredPermissions = ['instagram_basic', 'instagram_content_publish', 'pages_show_list'];
      const missingPermissions = requiredPermissions.filter(p => !grantedPermissions.includes(p));
      
      if (missingPermissions.length > 0) {
        setConnectionStatus(`âŒ Missing permissions: ${missingPermissions.join(', ')}. Your app needs Instagram permissions.`);
        console.error('[fetchInstagramAccounts] Missing required permissions:', missingPermissions);
      }
      
      // Get user info using shared SDK
      const userInfo = await facebookSDK.api('/me', { 
        access_token: accessToken, 
        fields: 'id,name,email' 
      });
      
      // Get Facebook pages with Instagram accounts using shared SDK
      const pagesResponse = await facebookSDK.api('/me/accounts', {
        access_token: accessToken,
        fields: 'id,name,instagram_business_account{id,username,profile_picture_url,followers_count,media_count}'
      });

      const pages = pagesResponse.data || [];
      const pagesWithInstagram = pages.filter(page => page.instagram_business_account);
      
      if (pagesWithInstagram.length === 0) {
        setConnectionStatus('âŒ No Instagram Business accounts found. Please connect an Instagram Business account to a Facebook Page first.');
        setIsConnecting(false);
        setInstagramConnected(false);
        return { mappedAccounts: [], userInfo };
      }

      const mappedAccounts = pagesWithInstagram.map(page => {
        const igAccount = page.instagram_business_account;
        return {
          id: igAccount.id,
          username: igAccount.username,
          profile_picture_url: igAccount.profile_picture_url || '',
          followers_count: igAccount.followers_count || 0,
          media_count: igAccount.media_count || 0,
          account_type: 'BUSINESS',
          page_id: page.id,
          page_name: page.name,
          access_token: accessToken
        };
      });

      setAvailableAccounts(mappedAccounts);
      
      // Account selection logic
      if (mappedAccounts.length === 1) {
        setSelectedAccount(mappedAccounts[0]);
        setConnectionStatus(`âœ… Connected successfully! Instagram account: @${mappedAccounts[0].username}`);
      } else if (mappedAccounts.length > 1) {
        setSelectedAccount(null);
        setConnectionStatus(`âœ… Connected successfully! ${mappedAccounts.length} Instagram accounts found. Please select an account below.`);
      }
      
      setInstagramConnected(true);
      setCardFlipped(true);
      setIsConnecting(false);
      return { mappedAccounts, userInfo };
    } catch (error) {
      console.error('Instagram API error:', error);
      setConnectionStatus('âŒ Failed to get Instagram data: ' + (error.message || 'Unknown error'));
      setIsConnecting(false);
      setInstagramConnected(false);
      return { mappedAccounts: [], userInfo: null };
    }
  };

  // Connect to backend
  const connectToBackend = async (accessToken, userInfo, mappedAccounts) => {
    try {
      const response = await apiClient.connectInstagram(
        accessToken,
        userInfo?.id,
        mappedAccounts
      );
      setConnectionStatus('âœ… Successfully connected Instagram to backend!');
      return response;
    } catch (error) {
      console.error('Backend connection error:', error);
      setConnectionStatus('âŒ Backend connection failed: ' + (error.message || 'Unknown error'));
      throw error;
    }
  };

  const handleAutoInputChange = (e) => {
    const { name, value } = e.target;
    setAutoFormData(prev => ({
      ...prev,
      [name]: value
    }));
  };

  const handleManualInputChange = (e) => {
    const { name, value } = e.target;
    setManualFormData(prev => ({
      ...prev,
      [name]: value
    }));
  };

  const generatePostContent = async () => {
    if (!autoFormData.prompt.trim()) {
      setConnectionStatus('âŒ Please enter a prompt for content generation');
      return;
    }

    setAutoFormData(prev => ({ ...prev, isGenerating: true }));
    setConnectionStatus('ðŸ¤– Generating Instagram caption...');

    try {
      const response = await apiClient.generateContent(autoFormData.prompt);
      
      if (response.success) {
        setAutoFormData(prev => ({
          ...prev,
          generatedContent: response.content,
          isGenerating: false
        }));
        setConnectionStatus('âœ… Content generated successfully! Review and publish below.');
      } else {
        throw new Error(response.error || 'Failed to generate content');
      }
    } catch (error) {
      console.error('Content generation error:', error);
      setConnectionStatus('âŒ Failed to generate content: ' + error.message);
      setAutoFormData(prev => ({ ...prev, isGenerating: false }));
    }
  };

  const publishPost = async () => {
    if (!selectedAccount) {
      setConnectionStatus('âŒ Please select an Instagram account first');
      return;
    }

    let caption, imageUrl;
    
    if (activeTab === 'auto') {
      if (!autoFormData.prompt.trim()) {
        setConnectionStatus('âŒ Please enter a prompt for content generation');
        return;
      }
      if (!autoFormData.imageUrl.trim()) {
        setConnectionStatus('âŒ Please enter an image URL for Instagram post');
        return;
      }
      caption = autoFormData.prompt;
      imageUrl = autoFormData.imageUrl;
    } else {
      if (!manualFormData.caption.trim()) {
        setConnectionStatus('âŒ Please enter a caption');
        return;
      }
      if (!manualFormData.imageUrl.trim()) {
        setConnectionStatus('âŒ Please enter an image URL for Instagram post');
        return;
      }
      caption = manualFormData.caption;
      imageUrl = manualFormData.imageUrl;
    }

    try {
      setIsPublishing(true);
      setConnectionStatus('ðŸš€ Publishing to Instagram...');

      const response = await apiClient.createInstagramPost(
        selectedAccount.id,
        caption,
        imageUrl,
        activeTab === 'auto' ? 'post-auto' : 'post-manual'
      );

      if (response.success) {
        setConnectionStatus(`âœ… Post published successfully to @${selectedAccount.username}!`);
        
        // Clear form data
        if (activeTab === 'auto') {
          setAutoFormData({
            prompt: '',
            imageUrl: '',
            generatedContent: '',
            isGenerating: false
          });
        } else {
          setManualFormData({
            caption: '',
            imageUrl: ''
          });
        }
      } else {
        throw new Error(response.message || 'Failed to publish post');
      }
    } catch (error) {
      console.error('Post publishing error:', error);
      setConnectionStatus('âŒ Failed to publish post: ' + error.message);
    } finally {
      setIsPublishing(false);
    }
  };

  const loginWithInstagram = async () => {
    // Check HTTPS requirement
    if (window.location.protocol !== 'https:' && window.location.hostname !== 'localhost') {
      setConnectionStatus('âŒ Instagram login requires HTTPS. Please use https://localhost:3001 or deploy with HTTPS');
      return;
    }

    // Check backend authentication
    try {
      await apiClient.getCurrentUser();
    } catch (error) {
      setConnectionStatus('âŒ Your session has expired. Please log out and log back in to connect Instagram.');
      setTimeout(() => {
        navigate('/');
      }, 3000);
      return;
    }

    setIsConnecting(true);

    try {
      let accessToken;
      
      // Check if already logged in to Facebook
      if (isAlreadyLoggedIn) {
        setConnectionStatus('ðŸ”„ Using existing Facebook login for Instagram...');
        accessToken = facebookSDK.getCurrentAccessToken();
        
        if (!accessToken) {
          // Force new login if no token available
          setIsAlreadyLoggedIn(false);
          throw new Error('No access token available from existing login');
        }
      } else {
        setConnectionStatus('ðŸ” Connecting to Instagram via Facebook...');
        
        // Required scopes for Instagram
        const instagramScopes = [
          'pages_show_list',
          'instagram_basic',
          'instagram_manage_comments',
          'instagram_manage_insights',
          'instagram_content_publish',
          'pages_read_engagement'
        ];

        const response = await facebookSDK.login(instagramScopes);
        accessToken = response.authResponse.accessToken;
      }

      setConnectionStatus('âœ… Facebook login successful! Checking Instagram accounts...');
      
      // Fetch Instagram accounts
      const { mappedAccounts, userInfo } = await fetchInstagramAccounts(accessToken);
      
      if (mappedAccounts.length > 0) {
        // Connect to backend
        await connectToBackend(accessToken, userInfo, mappedAccounts);
        setConnectionStatus(`âœ… Successfully connected ${mappedAccounts.length} Instagram account(s)!`);
      }

    } catch (error) {
      console.error('Instagram login error:', error);
      if (error.message.includes('User denied authorization')) {
        setConnectionStatus('âŒ Please authorize the app to continue and ensure you have Instagram Business accounts linked to your Facebook Pages.');
      } else {
        setConnectionStatus('âŒ Instagram login failed: ' + error.message);
      }
      setIsConnecting(false);
      setInstagramConnected(false);
    }
  };

  const handleAccountSelect = (account) => {
    setSelectedAccount(account);
    setConnectionStatus(`âœ… Selected Instagram account: @${account.username}`);
  };

  // Handle logout
  const handleLogout = async () => {
    await logout();
    navigate('/');
  };

  return (
    <div className="social-media-page">
      <div className="page-header">
        <div className="header-content">
          <h1>ðŸ“¸ Instagram Automation</h1>
          <button onClick={handleLogout} className="logout-btn">
            Logout
          </button>
        </div>
      </div>

      <div className="connection-card-container">
        <div className={`connection-card ${cardFlipped ? 'flipped' : ''}`}>
          <div className="card-face card-front">
            <div className="card-content">
              <div className="platform-icon">ðŸ“¸</div>
              <h2>Connect Instagram</h2>
              <p>Connect your Instagram Business account to start automating posts with AI-generated content.</p>
              
              <div className="features-list">
                <div className="feature">
                  <span className="feature-icon">ðŸ¤–</span>
                  <span>AI-Generated Captions</span>
                </div>
                <div className="feature">
                  <span className="feature-icon">ðŸ“Š</span>
                  <span>Analytics & Insights</span>
                </div>
                <div className="feature">
                  <span className="feature-icon">â°</span>
                  <span>Smart Scheduling</span>
                </div>
              </div>

              {isAlreadyLoggedIn && (
                <div className="existing-login-notice">
                  <span className="notice-icon">â„¹ï¸</span>
                  <span>Using existing Facebook login</span>
                </div>
              )}

              <button 
                onClick={loginWithInstagram}
                disabled={isConnecting}
                className="connect-button"
              >
                {isConnecting ? (
                  <span className="loading-spinner">â³</span>
                ) : (
                  <span>{isAlreadyLoggedIn ? 'Connect Instagram Accounts' : 'Connect Instagram'}</span>
                )}
              </button>

              {connectionStatus && (
                <div className={`status-message ${connectionStatus.includes('âœ…') ? 'success' : connectionStatus.includes('âŒ') ? 'error' : 'info'}`}>
                  {connectionStatus}
                </div>
              )}
            </div>
          </div>

          <div className="card-face card-back">
            <div className="card-content">
              <div className="connected-header">
                <div className="platform-icon">ðŸ“¸</div>
                <h2>Instagram Connected!</h2>
                
                {/* Account Selection */}
                {availableAccounts.length > 1 && !selectedAccount && (
                  <div className="account-selection">
                    <h3>Select Instagram Account:</h3>
                    <div className="accounts-grid">
                      {availableAccounts.map((account) => (
                        <div 
                          key={account.id} 
                          className="account-card selectable"
                          onClick={() => handleAccountSelect(account)}
                        >
                          <div className="account-info">
                            <div className="account-avatar">
                              {account.profile_picture_url ? (
                                <img src={account.profile_picture_url} alt={account.username} />
                              ) : (
                                <div className="avatar-placeholder">ðŸ“¸</div>
                              )}
                            </div>
                            <div className="account-details">
                              <div className="account-name">@{account.username}</div>
                              <div className="account-stats">
                                {account.followers_count.toLocaleString()} followers
                              </div>
                            </div>
                          </div>
                        </div>
                      ))}
                    </div>
                  </div>
                )}

                {/* Selected Account Display */}
                {selectedAccount && (
                  <div className="selected-account">
                    <div className="account-info">
                      <div className="account-avatar">
                        {selectedAccount.profile_picture_url ? (
                          <img src={selectedAccount.profile_picture_url} alt={selectedAccount.username} />
                        ) : (
                          <div className="avatar-placeholder">ðŸ“¸</div>
                        )}
                      </div>
                      <div className="account-details">
                        <div className="account-name">@{selectedAccount.username}</div>
                        <div className="account-stats">
                          {selectedAccount.followers_count.toLocaleString()} followers â€¢ {selectedAccount.media_count} posts
                        </div>
                      </div>
                    </div>
                  </div>
                )}
              </div>

              {/* Post Creation Interface */}
              {instagramConnected && selectedAccount && (
                <div className="post-interface">
                  <div className="tab-navigation">
                    <button 
                      className={`tab-button ${activeTab === 'auto' ? 'active' : ''}`}
                      onClick={() => setActiveTab('auto')}
                    >
                      <span className="tab-icon">ðŸ¤–</span>
                      AI Generated
                    </button>
                    <button 
                      className={`tab-button ${activeTab === 'manual' ? 'active' : ''}`}
                      onClick={() => setActiveTab('manual')}
                    >
                      <span className="tab-icon">âœï¸</span>
                      Manual Post
                    </button>
                  </div>

                  {activeTab === 'auto' && (
                    <div className="post-form auto-post">
                      <div className="form-group">
                        <label>
                          <MediaIcon type="text" />
                          Describe your post idea
                        </label>
                        <textarea
                          name="prompt"
                          value={autoFormData.prompt}
                          onChange={handleAutoInputChange}
                          placeholder="e.g., A beautiful sunset at the beach with golden hour lighting..."
                          rows={3}
                        />
                      </div>

                      <div className="form-group">
                        <label>
                          <MediaIcon type="photo" />
                          Image URL
                        </label>
                        <input
                          type="url"
                          name="imageUrl"
                          value={autoFormData.imageUrl}
                          onChange={handleAutoInputChange}
                          placeholder="https://example.com/image.jpg"
                        />
                      </div>

                      {autoFormData.generatedContent && (
                        <div className="generated-content">
                          <label>Generated Caption:</label>
                          <div className="content-preview">
                            {autoFormData.generatedContent}
                          </div>
                        </div>
                      )}

                      <div className="form-actions">
                        <button 
                          onClick={generatePostContent}
                          disabled={autoFormData.isGenerating || !autoFormData.prompt.trim()}
                          className="generate-button"
                        >
                          {autoFormData.isGenerating ? 'â³ Generating...' : 'ðŸ¤– Generate Caption'}
                        </button>

                        <button 
                          onClick={publishPost}
                          disabled={isPublishing || !autoFormData.prompt.trim() || !autoFormData.imageUrl.trim()}
                          className="publish-button"
                        >
                          {isPublishing ? 'â³ Publishing...' : 'ðŸš€ Publish Post'}
                        </button>
                      </div>
                    </div>
                  )}

                  {activeTab === 'manual' && (
                    <div className="post-form manual-post">
                      <div className="form-group">
                        <label>
                          <MediaIcon type="text" />
                          Caption
                        </label>
                        <textarea
                          name="caption"
                          value={manualFormData.caption}
                          onChange={handleManualInputChange}
                          placeholder="Write your Instagram caption here... #hashtags"
                          rows={4}
                        />
                      </div>

                      <div className="form-group">
                        <label>
                          <MediaIcon type="photo" />
                          Image URL
                        </label>
                        <input
                          type="url"
                          name="imageUrl"
                          value={manualFormData.imageUrl}
                          onChange={handleManualInputChange}
                          placeholder="https://example.com/image.jpg"
                        />
                      </div>

                      <div className="form-actions">
                        <button 
                          onClick={publishPost}
                          disabled={isPublishing || !manualFormData.caption.trim() || !manualFormData.imageUrl.trim()}
                          className="publish-button"
                        >
                          {isPublishing ? 'â³ Publishing...' : 'ðŸ“¸ Publish to Instagram'}
                        </button>
                      </div>
                    </div>
                  )}
                </div>
              )}

              {connectionStatus && (
                <div className={`status-message ${connectionStatus.includes('âœ…') ? 'success' : connectionStatus.includes('âŒ') ? 'error' : 'info'}`}>
                  {connectionStatus}
                </div>
              )}
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}

export default InstagramPage;
