import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import PlatformCard from './PlatformCard';
import ActivityLog from './ActivityLog';
import PlatformDetails from './PlatformDetails';
import useFacebookSDK from '../hooks/useFacebookSDK';
import './SocialMediaPage.css';

function SocialMediaPage() {
  const navigate = useNavigate();
  const [selectedPlatform, setSelectedPlatform] = useState(null);
  const [facebookConnected, setFacebookConnected] = useState(false);
  const [postData, setPostData] = useState({
    message: '',
    image: null,
    autoReplyComment: false,
    autoDMResponse: false
  });
  const [activityLog, setActivityLog] = useState([]);
  const [isCardFlipped, setIsCardFlipped] = useState(false);

  // Initialize Facebook SDK
  useFacebookSDK();

  const socialPlatforms = createSocialPlatforms(facebookConnected);
  const WEBHOOK_URL = "https://hook.eu2.make.com/m4f3qzhwhe4y62i9ghviwe4zw23hytio";


  const addActivity = (action, status) => {
    setActivityLog(prev => [...prev, {
      time: new Date().toLocaleString(),
      action,
      status
    }]);
  };

  const handleFacebookConnect = () => {
    setIsCardFlipped(true);
  };

  const handleFacebookLogin = async () => {
    try {
      const testPayload = {
        text: "ðŸš€ Hello from React UI!",
        pageId: "dummy_id",
        token: "dummy_token"
      };
  
      const webhookResponse = await fetch(WEBHOOK_URL, {
        method: "POST",
        headers: {
          "Content-Type": "application/json"
        },
        body: JSON.stringify(testPayload)
      });
  
      if (webhookResponse.ok) {
        setFacebookConnected(true);
        setIsCardFlipped(false);
        addActivity('âœ… Webhook test successful!', 'success');
      } else {
        throw new Error('âŒ Webhook test failed');
      }
    } catch (error) {
      console.error('Webhook test error:', error);
      addActivity('âŒ Webhook test error', 'error');
    }
  };
  
  
  const handlePlatformConnect = (platformId) => {
    if (platformId === 'facebook') {
      handleFacebookLogin();
    } else {
      // Handle other platform connections
      addActivity(`${platformId} connection initiated`, 'success');
    }
  };

  const selectedPlatformData = socialPlatforms.find(p => p.id === selectedPlatform);

  return (
    <div className="page-container">
      <header className="page-header">
        <button onClick={() => navigate('/')} className="back-btn">
          <svg width="20" height="20" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2">
            <polyline points="15,18 9,12 15,6"></polyline>
          </svg>
          Back to Dashboard
        </button>
        <div className="page-title-section">
          <h1 className="page-title">Social Media Automation</h1>
          <p className="page-subtitle">Connect and automate your social media platforms with advanced AI-powered features</p>
        </div>
      </header>

      <main className="page-main">
        <div className="platforms-container">
          <div className="platforms-grid">
            {socialPlatforms.map((platform) => (
              <PlatformCard
                key={platform.id}
                platform={platform}
                isSelected={selectedPlatform === platform.id}
                isFlipped={platform.id === 'facebook' && isCardFlipped}
                onSelect={setSelectedPlatform}
                onConnect={handlePlatformConnect}
                onFlip={handleFacebookConnect}
                postData={postData}
                setPostData={setPostData}
              />
            ))}
          </div>
        </div>

        <ActivityLog activityLog={activityLog} />

        {selectedPlatform && selectedPlatformData && (
          <PlatformDetails
            platform={selectedPlatformData}
            onClose={() => setSelectedPlatform(null)}
          />
        )}
      </main>
    </div>
  );
}

export default SocialMediaPage;
