import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { useAuth } from '../contexts/AuthContext';
import apiClient from '../services/apiClient';
import './SocialMediaPage.css';

function FacebookPage() {
  const navigate = useNavigate();
  const { logout } = useAuth();
  const [isConnecting, setIsConnecting] = useState(false);
  const [connectionStatus, setConnectionStatus] = useState('');
  const [selectedPage, setSelectedPage] = useState(null);
  const [availablePages, setAvailablePages] = useState([]);
  const [facebookConnected, setFacebookConnected] = useState(false);
  const [activeTab, setActiveTab] = useState('auto');
  const [cardFlipped, setCardFlipped] = useState(false);
  const [existingConnections, setExistingConnections] = useState(null);
  const [isLoggingOut, setIsLoggingOut] = useState(false);
  const [isCheckingStatus, setIsCheckingStatus] = useState(true);

  // Auto post states
  const [autoFormData, setAutoFormData] = useState({
    prompt: '',
    mediaType: 'none',
    mediaFile: null,
    generatedContent: '',
    isGenerating: false
  });

  // Scheduling states
  const [isScheduleMode, setIsScheduleMode] = useState(false);
  const [scheduleData, setScheduleData] = useState({
    prompt: '',
    time: '',
    frequency: 'daily', // daily, weekly, monthly
    isActive: false
  });

  // Manual post states
  const [manualFormData, setManualFormData] = useState({
    message: '',
    mediaType: 'none',
    mediaFile: null
  });

  // Publishing state
  const [isPublishing, setIsPublishing] = useState(false);

  // Posts history
  const [postHistory, setPostHistory] = useState([]);
  const [isLoadingPosts, setIsLoadingPosts] = useState(false);

  // Facebook App Configuration
  const FACEBOOK_APP_ID = process.env.REACT_APP_FACEBOOK_APP_ID || '1526961221410200';

  // Check for existing Facebook connections on component mount
  useEffect(() => {
    checkExistingFacebookConnections();
  }, []);

  const checkExistingFacebookConnections = async () => {
    try {
      setIsCheckingStatus(true);
      const response = await apiClient.getFacebookStatus();

      if (response.connected) {
        setExistingConnections(response);
        setFacebookConnected(true);

        // Convert backend data to frontend format for compatibility
        const pagesFromBackend = response.accounts.pages.map(page => ({
          id: page.platform_id,
          name: page.name,
          category: page.category,
          access_token: '', // Don't expose tokens in frontend
          profilePicture: page.profile_picture || '',
          canPost: page.can_post,
          canComment: page.can_comment,
          followerCount: page.follower_count
        }));

        setAvailablePages(pagesFromBackend);

        if (pagesFromBackend.length === 1) {
          setSelectedPage(pagesFromBackend[0]);
        }

        setCardFlipped(true);
        setConnectionStatus(`‚úÖ Already connected! ${response.pages_count} Facebook page(s) available.`);
      } else {
        setConnectionStatus('üì± Ready to connect your Facebook account');
      }
    } catch (error) {
      console.error('Error checking Facebook status:', error);
      setConnectionStatus('‚ö†Ô∏è Unable to check Facebook connection status');
    } finally {
      setIsCheckingStatus(false);
    }
  };

  const handleFacebookLogout = async () => {
    try {
      setIsLoggingOut(true);
      setConnectionStatus('üîÑ Disconnecting from Facebook...');

      const response = await apiClient.logoutFacebook();

      // Reset all Facebook-related state
      setFacebookConnected(false);
      setExistingConnections(null);
      setAvailablePages([]);
      setSelectedPage(null);
      setCardFlipped(false);
      setPostHistory([]);

      setConnectionStatus('‚úÖ Successfully disconnected from Facebook');

      // Clear the message after a few seconds
      setTimeout(() => {
        setConnectionStatus('üì± Ready to connect your Facebook account');
      }, 3000);

    } catch (error) {
      console.error('Error logging out from Facebook:', error);
      setConnectionStatus('‚ùå Failed to disconnect from Facebook: ' + error.message);
    } finally {
      setIsLoggingOut(false);
    }
  };

  // Load post history
  const loadPostHistory = async () => {
    if (!selectedPage) return;

    try {
      setIsLoadingPosts(true);
      const response = await apiClient.getSocialPosts('facebook');
      setPostHistory(response.slice(0, 10)); // Show last 10 posts
    } catch (error) {
      console.error('Error loading post history:', error);
    } finally {
      setIsLoadingPosts(false);
    }
  };

  // Load posts when page is selected
  useEffect(() => {
    if (selectedPage && facebookConnected) {
      loadPostHistory();
    }
  }, [selectedPage, facebookConnected]);

  // Handle schedule activation/deactivation
  const handleScheduleToggle = async () => {
    if (!selectedPage) {
      setConnectionStatus('‚ùå Please select a page first');
      return;
    }

    if (!scheduleData.prompt || !scheduleData.time) {
      setConnectionStatus('‚ùå Please fill in prompt and time before activating schedule');
      return;
    }

    try {
      if (!scheduleData.isActive) {
        // Activating schedule - create scheduled post
        setConnectionStatus('üîÑ Creating scheduled post...');

        const response = await apiClient.createScheduledPost({
          prompt: scheduleData.prompt,
          post_time: scheduleData.time,
          frequency: scheduleData.frequency,
          social_account_id: selectedPage.id
        });

        setScheduleData(prev => ({ 
          ...prev, 
          isActive: true,
          scheduleId: response.data.id
        }));

        setConnectionStatus('‚úÖ Schedule activated successfully! Posts will be published automatically.');

        // Load post history to show scheduled posts
        setTimeout(() => {
          loadPostHistory();
        }, 1000);

      } else {
        // Deactivating schedule - delete scheduled post
        if (scheduleData.scheduleId) {
          setConnectionStatus('üîÑ Deactivating schedule...');

          await apiClient.deleteScheduledPost(scheduleData.scheduleId);

          setConnectionStatus('‚úÖ Schedule deactivated successfully');
        }

        setScheduleData(prev => ({ 
          ...prev, 
          isActive: false,
          scheduleId: null
        }));
      }

    } catch (error) {
      console.error('Schedule toggle error:', error);
      setConnectionStatus('‚ùå Failed to update schedule: ' + (error.message || 'Unknown error'));
    }
  };

  // Icon Components
  const MediaIcon = ({ type }) => {
    switch(type) {
      case 'photo':
        return (
          <svg width="24" height="24" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2">
            <rect x="3" y="3" width="18" height="18" rx="2" ry="2"/>
            <circle cx="8.5" cy="8.5" r="1.5"/>
            <polyline points="21,15 16,10 5,21"/>
          </svg>
        );
      case 'video':
        return (
          <svg width="24" height="24" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2">
            <polygon points="23 7 16 12 23 17 23 7"/>
            <rect x="1" y="5" width="15" height="14" rx="2" ry="2"/>
          </svg>
        );
      default:
        return (
          <svg width="24" height="24" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2">
            <path d="M21 15a2 2 0 0 1-2 2H7l-4 4V5a2 2 0 0 1 2-2h14a2 2 0 0 1 2 2z"/>
          </svg>
        );
    }
  };

  // fetchPages function
  const fetchPages = async (accessToken) => {
    if (!accessToken) {
      setConnectionStatus('‚ùå No Facebook access token found. Please try reconnecting.');
      setIsConnecting(false);
      setFacebookConnected(false);
      return { mappedPages: [], userInfo: null };
    }

    try {
      // Check permissions
      const permissionsResponse = await new Promise((resolve, reject) => {
        window.FB.api('/me/permissions', { access_token: accessToken }, (response) => {
          if (response.error) reject(new Error(response.error.message));
          else resolve(response);
        });
      });

      const grantedPermissions = permissionsResponse.data?.filter(p => p.status === 'granted').map(p => p.permission) || [];
      const requiredPermissions = ['pages_show_list', 'pages_manage_posts'];
      const missingPermissions = requiredPermissions.filter(p => !grantedPermissions.includes(p));

      if (missingPermissions.length > 0) {
        setConnectionStatus(`‚ùå Missing permissions: ${missingPermissions.join(', ')}. Your app needs "Pages API" permissions.`);
        console.error('[fetchPages] Missing required permissions:', missingPermissions);
      }

      // Get user info
      const userInfo = await new Promise((resolve, reject) => {
        window.FB.api('/me', { access_token: accessToken, fields: 'id,name,email' }, (response) => {
          if (response.error) reject(new Error(response.error.message));
          else resolve(response);
        });
      });

      // Get user's pages
      const pagesResponse = await new Promise((resolve, reject) => {
        window.FB.api('/me/accounts', {
          access_token: accessToken,
          fields: 'id,name,category,access_token,picture,fan_count,tasks'
        }, (response) => {
          if (response.error) {
            reject(new Error(`${response.error.message} (Code: ${response.error.code})`));
          } else {
            resolve(response);
          }
        });
      });

      const pages = pagesResponse.data || [];
      const mappedPages = pages.map(page => {
        const tasks = page.tasks || [];
        const canPost = tasks.includes('CREATE_CONTENT') || tasks.includes('MANAGE');
        const canComment = tasks.includes('MODERATE') || tasks.includes('MANAGE');

        return {
          id: page.id,
          name: page.name,
          category: page.category || 'Page',
          access_token: page.access_token,
          profilePicture: page.picture?.data?.url || '',
          canPost: canPost,
          canComment: canComment,
          followerCount: page.fan_count || 0
        };
      });

      setAvailablePages(mappedPages);

      // Page selection logic
      if (mappedPages.length === 1) {
        setSelectedPage(mappedPages[0]);
        setConnectionStatus(`‚úÖ Connected successfully! 1 page found.`);
      } else if (mappedPages.length > 1) {
        setSelectedPage(null);
        setConnectionStatus(`‚úÖ Connected successfully! ${mappedPages.length} pages found. Please select a page below.`);
      } else {
        // Fallback to user profile if no pages available
        setSelectedPage({
          id: userInfo.id,
          name: userInfo.name,
          access_token: accessToken,
          category: 'Personal Profile',
          profilePicture: '',
          canPost: true,
          canComment: true,
          followerCount: 0
        });
        setConnectionStatus('‚úÖ Connected as personal profile (no pages found).');
      }
      setFacebookConnected(true);
      setCardFlipped(true);
      setIsConnecting(false);
      return { mappedPages, userInfo };
    } catch (error) {
      console.error('Facebook API error:', error);
      setConnectionStatus('‚ùå Failed to get Facebook data: ' + (error.message || 'Unknown error'));
      setIsConnecting(false);
      setFacebookConnected(false);
      return { mappedPages: [], userInfo: null };
    }
  };

  // Connect to backend
  const connectToBackend = async (accessToken, userInfo, mappedPages) => {
    try {
      const response = await apiClient.connectFacebook(
        accessToken,
        userInfo?.id,
        mappedPages
      );
      setConnectionStatus('‚úÖ Successfully connected to backend!');
      return response;
    } catch (error) {
      console.error('Backend connection error:', error);
      setConnectionStatus('‚ùå Backend connection failed: ' + (error.message || 'Unknown error'));
      throw error;
    }
  };

  const handleAutoInputChange = (e) => {
    const { name, value, files } = e.target;
    setAutoFormData(prev => ({
      ...prev,
      [name]: files ? files[0] : value
    }));
  };

  const handleManualInputChange = (e) => {
    const { name, value, files } = e.target;
    setManualFormData(prev => ({
      ...prev,
      [name]: files ? files[0] : value
    }));
  };

  const handleMediaTypeChange = (type, formType) => {
    if (formType === 'auto') {
      setAutoFormData(prev => ({
        ...prev,
        mediaType: type,
        mediaFile: null
      }));
    } else {
      setManualFormData(prev => ({
        ...prev,
        mediaType: type,
        mediaFile: null
      }));
    }
  };

  // Generate content using AI
  const generatePostContent = async () => {
    if (!autoFormData.prompt.trim()) {
      setConnectionStatus('‚ùå Please enter a prompt for AI generation');
      return;
    }

    setAutoFormData(prev => ({ ...prev, isGenerating: true }));
    setConnectionStatus('ü§ñ Generating content with AI...');

    try {
      const response = await apiClient.generateContent(autoFormData.prompt);
      setAutoFormData(prev => ({
        ...prev,
        generatedContent: response.content,
        isGenerating: false
      }));
      setConnectionStatus('‚úÖ Content generated successfully!');
    } catch (error) {
      console.error('Content generation error:', error);
      setConnectionStatus('‚ùå Failed to generate content: ' + (error.message || 'Unknown error'));
      setAutoFormData(prev => ({ ...prev, isGenerating: false }));
    }
  };

  // Publish post to Facebook
  const publishPost = async () => {
    if (!selectedPage) {
      setConnectionStatus('‚ùå Please select a page first');
      return;
    }

    const isAutoMode = activeTab === 'auto';
    const content = isAutoMode ? autoFormData.generatedContent : manualFormData.message;
    const mediaType = isAutoMode ? autoFormData.mediaType : manualFormData.mediaType;
    const mediaFile = isAutoMode ? autoFormData.mediaFile : manualFormData.mediaFile;

    if (!content || content.trim() === '') {
      setConnectionStatus('‚ùå Please enter some content for your post');
      return;
    }

    if (mediaType !== 'none' && !mediaFile) {
      setConnectionStatus('‚ùå Please select a media file to upload');
      return;
    }

    try {
      setIsPublishing(true);
      setConnectionStatus('üîÑ Publishing post to Facebook...');

      // Ensure Facebook SDK is initialized and ready
      if (!window.FB) {
        console.log('üîÑ Facebook SDK not found, initializing...');
        await loadFacebookSDK();
      }

      // Verify SDK is properly initialized
      if (!window.FB || !window.FB.api || !window.FB.getLoginStatus) {
        throw new Error('Facebook SDK not properly initialized');
      }

      // Check login status before posting
      const loginStatus = await new Promise((resolve) => {
        window.FB.getLoginStatus(resolve, true);
      });

      if (loginStatus.status !== 'connected') {
        throw new Error('Facebook login session expired. Please reconnect your account.');
      }

      console.log('‚úÖ Facebook SDK ready for posting');

      if (mediaType === 'none') {
        await postTextOnly(content);
      } else {
        await postWithMedia(content, mediaFile, mediaType);
      }

      setConnectionStatus('‚úÖ Post published successfully!');

      // Reload post history
      setTimeout(() => {
        loadPostHistory();
      }, 1000);

      // Clear form data
      if (isAutoMode) {
        setAutoFormData(prev => ({
          ...prev,
          prompt: '',
          generatedContent: '',
          mediaFile: null,
          mediaType: 'none'
        }));
      } else {
        setManualFormData(prev => ({
          ...prev,
          message: '',
          mediaFile: null,
          mediaType: 'none'
        }));
      }

    } catch (error) {
      console.error('Post publishing error:', error);
      setConnectionStatus('‚ùå Failed to publish post: ' + (error.message || 'Unknown error'));
    } finally {
      setIsPublishing(false);
    }
  };

  // Post text-only content
  const postTextOnly = async (message) => {
    return new Promise((resolve, reject) => {
      window.FB.api(`/${selectedPage.id}/feed`, 'POST', {
        message: message,
        access_token: selectedPage.access_token
      }, (response) => {
        if (response.error) {
          reject(new Error(`${response.error.message} (Code: ${response.error.code})`));
        } else {
          resolve(response);
        }
      });
    });
  };

  // Post content with media
  const postWithMedia = async (message, file, mediaType) => {
    const fileData = await fileToBase64(file);

    if (mediaType === 'photo') {
      return new Promise((resolve, reject) => {
        window.FB.api(`/${selectedPage.id}/photos`, 'POST', {
          caption: message,
          source: fileData,
          access_token: selectedPage.access_token
        }, (response) => {
          if (response.error) {
            reject(new Error(`${response.error.message} (Code: ${response.error.code})`));
          } else {
            resolve(response);
          }
        });
      });
    } else if (mediaType === 'video') {
      return new Promise((resolve, reject) => {
        window.FB.api(`/${selectedPage.id}/videos`, 'POST', {
          description: message,
          source: fileData,
          access_token: selectedPage.access_token
        }, (response) => {
          if (response.error) {
            reject(new Error(`${response.error.message} (Code: ${response.error.code})`));
          } else {
            resolve(response);
          }
        });
      });
    }
  };

  // Convert file to base64
  const fileToBase64 = (file) => {
    return new Promise((resolve, reject) => {
      const reader = new FileReader();
      reader.readAsDataURL(file);
      reader.onload = () => resolve(reader.result);
      reader.onerror = (error) => reject(error);
    });
  };

  const loginWithFacebook = async () => {
    // Check HTTPS requirement
    if (window.location.protocol !== 'https:' && window.location.hostname !== 'localhost') {
      setConnectionStatus('‚ùå Facebook login requires HTTPS. Please use https://localhost:3001 or deploy with HTTPS');
      return;
    }

    // Check backend authentication
    try {
      await apiClient.getCurrentUser();
    } catch (error) {
      setConnectionStatus('‚ùå Your session has expired. Please log out and log back in to connect Facebook.');
      setTimeout(() => {
        logout();
        navigate('/');
      }, 3000);
      return;
    }

    setIsConnecting(true);
    setConnectionStatus('üì¶ Loading Facebook SDK...');

    try {
      await loadFacebookSDK();

      if (!window.FB || typeof window.FB.login !== 'function') {
        setConnectionStatus('‚ùå Facebook SDK failed to load. Please refresh the page and try again.');
        setIsConnecting(false);
        return;
      }

      setConnectionStatus('üîê Connecting to Facebook...');

      window.FB.login((response) => {
        if (response.authResponse && response.authResponse.accessToken) {
          setConnectionStatus('‚úÖ Facebook login successful! Fetching pages...');

          (async () => {
            try {
              const { mappedPages, userInfo } = await fetchPages(response.authResponse.accessToken);

              if (mappedPages.length > 0) {
                setConnectionStatus('üîÑ Connecting to backend...');
                const backendResponse = await connectToBackend(response.authResponse.accessToken, userInfo, mappedPages);

                if (backendResponse?.data?.pages) {
                  setAvailablePages(mappedPages);
                  setConnectionStatus(`‚úÖ Connected successfully! ${backendResponse.data.pages_connected} pages synchronized with backend.`);
                }
              } else {
                setConnectionStatus('üîÑ No pages found in Facebook API. Connecting to backend to check for existing connections...');

                try {
                  await connectToBackend(response.authResponse.accessToken, userInfo, []);

                  const existingAccounts = await apiClient.getSocialAccounts();
                  const facebookPages = existingAccounts.filter(acc => 
                    acc.platform === 'facebook' && acc.account_type === 'page' && acc.is_connected
                  );

                  if (facebookPages.length > 0) {
                    const backendMappedPages = facebookPages.map(page => ({
                      id: page.platform_user_id,
                      name: page.display_name,
                      category: page.platform_data?.category || 'Page',
                      access_token: page.access_token || response.authResponse.accessToken,
                      profilePicture: page.profile_picture_url || '',
                      canPost: page.platform_data?.can_post !== false,
                      canComment: page.platform_data?.can_comment !== false,
                      followerCount: page.follower_count || 0
                    }));

                    setAvailablePages(backendMappedPages);
                    setConnectionStatus(`‚úÖ Found ${facebookPages.length} existing page(s) from previous connections!`);
                  } else {
                    setConnectionStatus('‚ö†Ô∏è No pages found. You may need to grant page permissions or create a Facebook page first.');
                  }
                } catch (backendError) {
                  console.error('[FB.login] Backend check failed:', backendError);
                  setConnectionStatus('‚ö†Ô∏è No pages found. You may need to grant page permissions or create a Facebook page first.');
                }
              }
            } catch (error) {
              console.error('[FB.login] Error in page fetching or backend connection:', error);
              setConnectionStatus('‚ùå Error during setup: ' + (error.message || 'Unknown error'));
              setIsConnecting(false);
              setFacebookConnected(false);
            }
          })();
        } else {
          if (response.status === 'not_authorized') {
            setConnectionStatus('‚ùå Please authorize the app to continue and select at least one page.');
          } else {
            setConnectionStatus('‚ùå Facebook login cancelled or failed');
          }
          setIsConnecting(false);
        }
      }, {
        scope: [
          'public_profile',
          'pages_show_list',
          'pages_read_engagement', 
          'pages_manage_posts',
          'pages_manage_engagement',
          'pages_read_user_content',
          'pages_manage_metadata'
        ].join(','),
        enable_profile_selector: true,
        return_scopes: true,
        auth_type: 'rerequest',
        display: 'popup'
      });
    } catch (error) {
      console.error('Facebook login error:', error);
      setConnectionStatus('‚ùå Facebook login failed: ' + error.message);
    }
  };

  // Clean up Facebook SDK
  const cleanupFacebookSDK = () => {
    // Remove existing script
    const existingScript = document.getElementById('facebook-jssdk');
    if (existingScript) {
      existingScript.remove();
    }

    // Clear Facebook objects
    if (window.FB) {
      try {
        // Try to logout first if logged in
        if (window.FB.getLoginStatus) {
          window.FB.logout();
        }
      } catch (e) {
        // Ignore errors during cleanup
      }
      delete window.FB;
    }

    if (window.fbAsyncInit) {
      delete window.fbAsyncInit;
    }

    // Remove any fb-root divs
    const fbRoots = document.querySelectorAll('#fb-root');
    fbRoots.forEach(root => root.remove());
  };

  // Load Facebook SDK with multiple version fallbacks
  const loadFacebookSDK = () => {
    return new Promise((resolve, reject) => {
      cleanupFacebookSDK();

      // Try multiple API versions in order of preference
      const versions = ['v21.0', 'v20.0', 'v19.0', 'v18.0'];
      let currentVersionIndex = 0;

      const tryInitialization = () => {
        const version = versions[currentVersionIndex];

        window.fbAsyncInit = function () {
          try {
            window.FB.init({
              appId: FACEBOOK_APP_ID,
              cookie: true,
              xfbml: true,
              version: version,
              status: true
            });

            console.log(`‚úÖ Facebook SDK initialized successfully with ${version}`);

            // Test if the SDK is working
            window.FB.getLoginStatus(function(response) {
              console.log('‚úÖ Facebook SDK test successful:', response.status);
              resolve();
            }, true);

          } catch (error) {
            console.error(`‚ùå Facebook SDK initialization failed with ${version}:`, error);

            // Try next version
            currentVersionIndex++;
            if (currentVersionIndex < versions.length) {
              console.log(`üîÑ Trying Facebook SDK version ${versions[currentVersionIndex]}...`);
              setTimeout(tryInitialization, 500);
            } else {
              reject(new Error('All Facebook SDK versions failed to initialize'));
            }
          }
        };

        // Remove existing script if any
        const existingScript = document.getElementById('facebook-jssdk');
        if (existingScript) {
          existingScript.remove();
        }

        const script = document.createElement('script');
        script.id = 'facebook-jssdk';
        script.src = 'https://connect.facebook.net/en_US/sdk.js';
        script.async = true;
        script.defer = true;
        script.crossOrigin = 'anonymous';
        script.onerror = () => {
          console.error(`‚ùå Failed to load Facebook SDK script for ${version}`);

          // Try next version
          currentVersionIndex++;
          if (currentVersionIndex < versions.length) {
            console.log(`üîÑ Trying Facebook SDK version ${versions[currentVersionIndex]}...`);
            setTimeout(tryInitialization, 500);
          } else {
            reject(new Error('Failed to load Facebook SDK script'));
          }
        };

        document.body.appendChild(script);
      };

      tryInitialization();
    });
  };

  return (
    <>
      <div id="fb-root"></div>
      <div style={{ 
        minHeight: '100vh',
        background: '#ffffff',
        padding: '0',
        display: 'flex',
        flexDirection: 'column'
      }}>
      {/* Navigation Header */}
      <div className="glass-effect" style={{
        padding: '1.5rem 2rem',
        margin: '1.5rem',
        borderRadius: '25px',
        display: 'flex',
        justifyContent: 'space-between',
        alignItems: 'center',
        background: 'rgba(24, 119, 242, 0.05)',
        backdropFilter: 'blur(25px)',
        WebkitBackdropFilter: 'blur(25px)',
        border: '1px solid rgba(24, 119, 242, 0.1)',
        boxShadow: '0 15px 35px rgba(24, 119, 242, 0.08)',
        position: 'sticky',
        top: '1.5rem',
        zIndex: 100
      }}>
        <div style={{ display: 'flex', alignItems: 'center', gap: '1rem' }}>
          <div style={{
            padding: '0.75rem',
            borderRadius: '15px',
            background: facebookConnected ? 'rgba(34, 197, 94, 0.2)' : 'rgba(24, 119, 242, 0.2)',
            display: 'flex',
            alignItems: 'center',
            justifyContent: 'center',
            transition: 'all 0.3s ease',
            position: 'relative'
          }}>
            <svg width="24" height="24" viewBox="0 0 24 24" fill={facebookConnected ? "#22c55e" : "#1877f2"}>
              <path d="M24 12.073c0-6.627-5.373-12-12-12s-12 5.373-12 12c0 5.99 4.388 10.954 10.125 11.854v-8.385H7.078v-3.47h3.047V9.43c0-3.007 1.792-4.669 4.533-4.669 1.312 0 2.686.235 2.686.235v2.953H15.83c-1.491 0-1.956.925-1.956 1.874v2.25h3.328l-.532 3.47h-2.796v8.385C19.612 23.027 24 18.062 24 12.073z"/>
            </svg>
            {facebookConnected && (
              <div style={{
                position: 'absolute',
                top: '6px',
                right: '6px',
                width: '10px',
                height: '10px',
                borderRadius: '50%',
                background: '#22c55e',
                boxShadow: '0 0 0 2px white'
              }} />
            )}
          </div>
          <div>
            <h1 style={{ 
              margin: 0, 
              fontSize: '1.5rem', 
              fontWeight: '700', 
              color: '#1a202c',
              textShadow: '0 1px 2px rgba(0, 0, 0, 0.05)'
            }}>
              {facebookConnected ? 
                (selectedPage ? `${selectedPage.name}` : 'Facebook Manager') : 
                'Facebook Manager'
              }
            </h1>
            <div style={{ 
              margin: 0, 
              fontSize: '0.875rem', 
              color: '#4a5568',
              textShadow: 'none',
              display: 'flex',
              alignItems: 'center',
              gap: '0.5rem'
            }}>
              {facebookConnected ? (
                selectedPage ? (
                  <>
                    <div style={{
                      width: '8px',
                      height: '8px',
                      borderRadius: '50%',
                      background: '#22c55e'
                    }} />
                    <span>
                      {selectedPage.category} ‚Ä¢ {selectedPage.follower_count || 0} followers
                    </span>
                  </>
                ) : (
                  <>
                    <div style={{
                      width: '8px',
                      height: '8px',
                      borderRadius: '50%',
                      background: '#f59e0b'
                    }} />
                    <span>Connected ‚Ä¢ Select a page to continue</span>
                  </>
                )
              ) : (
                <>
                  <div style={{
                    width: '8px',
                    height: '8px',
                    borderRadius: '50%',
                    background: '#6b7280'
                  }} />
                  <span>Not connected ‚Ä¢ Ready to link your account</span>
                </>
              )}
            </div>
          </div>
        </div>

        <div style={{ display: 'flex', gap: '1rem', alignItems: 'center' }}>
          {/* Dynamic Status Indicator */}
          {facebookConnected && selectedPage && (
            <div style={{
              padding: '0.5rem 1rem',
              background: 'rgba(34, 197, 94, 0.1)',
              border: '1px solid rgba(34, 197, 94, 0.2)',
              borderRadius: '25px',
              display: 'flex',
              alignItems: 'center',
              gap: '0.5rem',
              fontSize: '0.75rem',
              fontWeight: '600',
              color: '#065f46'
            }}>
              <div style={{
                width: '6px',
                height: '6px',
                borderRadius: '50%',
                background: '#22c55e',
                animation: 'pulse 2s infinite'
              }} />
              Live
            </div>
          )}

          {/* Quick Actions */}
          {facebookConnected && selectedPage && (
            <div style={{ display: 'flex', gap: '0.5rem' }}>
              <button
                onClick={() => window.open(`https://facebook.com/${selectedPage.id}`, '_blank')}
                style={{
                  padding: '0.5rem',
                  background: 'rgba(24, 119, 242, 0.1)',
                  border: '1px solid rgba(24, 119, 242, 0.2)',
                  borderRadius: '10px',
                  color: '#1877f2',
                  cursor: 'pointer',
                  display: 'flex',
                  alignItems: 'center',
                  transition: 'all 0.3s ease'
                }}
                title="View Page"
              >
                <svg width="14" height="14" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2">
                  <path d="M18 13v6a2 2 0 0 1-2 2H5a2 2 0 0 1-2-2V8a2 2 0 0 1 2-2h6"/>
                  <polyline points="15,3 21,3 21,9"/>
                  <line x1="10" y1="14" x2="21" y2="3"/>
                </svg>
              </button>

              <button
                onClick={loadPostHistory}
                disabled={isLoadingPosts}
                style={{
                  padding: '0.5rem',
                  background: 'rgba(59, 130, 246, 0.1)',
                  border: '1px solid rgba(59, 130, 246, 0.2)',
                  borderRadius: '10px',
                  color: '#3b82f6',
                  cursor: 'pointer',
                  display: 'flex',
                  alignItems: 'center',
                  transition: 'all 0.3s ease',
                  opacity: isLoadingPosts ? 0.7 : 1
                }}
                title="Refresh Posts"
              >
                <svg width="14" height="14" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" style={{ transform: isLoadingPosts ? 'rotate(360deg)' : 'none', transition: 'transform 1s ease' }}>
                  <path d="M23 4v6h-6"/>
                  <path d="M20.49 15a9 9 0 1 1-2.12-9.36L23 10"/>
                </svg>
              </button>
            </div>
          )}

          {/* Facebook Disconnect Button - Only show when connected */}
          {facebookConnected && (
            <button 
              onClick={handleFacebookLogout}
              disabled={isLoggingOut}
              style={{
                padding: '0.875rem 1.5rem',
                background: isLoggingOut ? 
                  'rgba(239, 68, 68, 0.6)' : 
                  'rgba(239, 68, 68, 0.1)',
                border: '1px solid rgba(239, 68, 68, 0.2)',
                borderRadius: '15px',
                color: isLoggingOut ? 'white' : '#dc2626',
                cursor: 'pointer',
                display: 'flex',
                alignItems: 'center',
                gap: '0.5rem',
                fontWeight: '600',
                fontSize: '0.875rem',
                transition: 'all 0.3s ease',
                backdropFilter: 'blur(10px)',
                WebkitBackdropFilter: 'blur(10px)',
                textShadow: 'none',
                opacity: isLoggingOut ? 0.7 : 1
              }}
              onMouseEnter={(e) => {
                if (!isLoggingOut) {
                  e.target.style.background = 'rgba(239, 68, 68, 0.15)';
                  e.target.style.transform = 'translateY(-2px)';
                  e.target.style.boxShadow = '0 8px 25px rgba(239, 68, 68, 0.15)';
                }
              }}
              onMouseLeave={(e) => {
                if (!isLoggingOut) {
                  e.target.style.background = 'rgba(239, 68, 68, 0.1)';
                  e.target.style.transform = 'translateY(0)';
                  e.target.style.boxShadow = 'none';
                }
              }}
            >
              <svg width="16" height="16" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2">
                <path d="M9 21H5a2 2 0 0 1-2-2V5a2 2 0 0 1 2-2h4"/>
                <polyline points="16,17 21,12 16,7"/>
                <line x1="21" y1="12" x2="9" y2="12"/>
