                          <textarea
                            value={autoFormData.generatedContent}
                            onChange={(e) => setAutoFormData(prev => ({ ...prev, generatedContent: e.target.value }))}
                            style={{
                              width: '100%',
                              minHeight: '140px',
                              padding: '1.25rem',
                              borderRadius: '15px',
                              border: '2px solid rgba(16, 185, 129, 0.3)',
                              background: 'rgba(16, 185, 129, 0.05)',
                              color: '#1a202c',
                              resize: 'vertical',
                              fontSize: '1rem',
                              fontFamily: 'inherit',
                              transition: 'all 0.3s ease'
                            }}
                          />
                        </div>
                      )}
                    </div>
                  ) : activeTab === 'schedule' ? (
                    /* Schedule Mode */
                    <div style={{ display: 'flex', flexDirection: 'column', gap: '1.5rem' }}>
                      <div style={{
                        padding: '1.5rem',
                        background: 'rgba(168, 85, 247, 0.1)',
                        borderRadius: '15px',
                        border: '1px solid rgba(168, 85, 247, 0.2)'
                      }}>
                        <h3 style={{ 
                          margin: '0 0 1rem 0', 
                          color: '#7c3aed',
                          fontSize: '1.125rem',
                          fontWeight: '600'
                        }}>
                          ‚è∞ Automated Post Scheduling
                        </h3>
                        <p style={{ 
                          margin: '0',
                          color: '#6b7280',
                          fontSize: '0.875rem'
                        }}>
                          Set up recurring posts with AI-generated content based on your prompt
                        </p>
                      </div>

                      {/* Schedule Prompt */}
                      <div>
                        <label style={{ 
                          display: 'block', 
                          marginBottom: '0.5rem', 
                          fontWeight: '600',
                          color: '#4a5568',
                          fontSize: '0.875rem'
                        }}>
                          üìù Content Theme/Prompt
                        </label>
                        <textarea
                          value={scheduleData.prompt}
                          onChange={(e) => setScheduleData(prev => ({ ...prev, prompt: e.target.value }))}
                          placeholder="Enter the theme for your scheduled posts (e.g., 'daily motivational quotes', 'logical questions', 'fitness tips')..."
                          style={{
                            width: '100%',
                            minHeight: '100px',
                            padding: '1rem',
                            borderRadius: '12px',
                            border: '2px solid rgba(226, 232, 240, 0.8)',
                            background: 'rgba(255, 255, 255, 0.9)',
                            color: '#1a202c',
                            resize: 'vertical',
                            fontSize: '1rem',
                            fontFamily: 'inherit',
                            transition: 'all 0.3s ease'
                          }}
                        />
                      </div>

                      {/* Schedule Time */}
                      <div style={{ display: 'grid', gridTemplateColumns: '1fr 1fr', gap: '1rem' }}>
                        <div>
                          <label style={{ 
                            display: 'block', 
                            marginBottom: '0.5rem', 
                            fontWeight: '600',
                            color: '#4a5568',
                            fontSize: '0.875rem'
                          }}>
                            üïê Post Time
                          </label>
                          <input
                            type="time"
                            value={scheduleData.time}
                            onChange={(e) => setScheduleData(prev => ({ ...prev, time: e.target.value }))}
                            style={{
                              width: '100%',
                              padding: '0.875rem',
                              borderRadius: '12px',
                              border: '2px solid rgba(226, 232, 240, 0.8)',
                              background: 'rgba(255, 255, 255, 0.9)',
                              color: '#1a202c',
                              fontSize: '1rem',
                              fontFamily: 'inherit'
                            }}
                          />
                        </div>

                        <div>
                          <label style={{ 
                            display: 'block', 
                            marginBottom: '0.5rem', 
                            fontWeight: '600',
                            color: '#4a5568',
                            fontSize: '0.875rem'
                          }}>
                            üîÑ Frequency
                          </label>
                          <select
                            value={scheduleData.frequency}
                            onChange={(e) => setScheduleData(prev => ({ ...prev, frequency: e.target.value }))}
                            style={{
                              width: '100%',
                              padding: '0.875rem',
                              borderRadius: '12px',
                              border: '2px solid rgba(226, 232, 240, 0.8)',
                              background: 'rgba(255, 255, 255, 0.9)',
                              color: '#1a202c',
                              fontSize: '1rem',
                              fontFamily: 'inherit'
                            }}
                          >
                            <option value="daily">Daily</option>
                            <option value="weekly">Weekly</option>
                            <option value="monthly">Monthly</option>
                          </select>
                        </div>
                      </div>

                      {/* Schedule Status */}
                      <div style={{
                        padding: '1rem',
                        background: scheduleData.isActive ? 'rgba(34, 197, 94, 0.1)' : 'rgba(156, 163, 175, 0.1)',
                        borderRadius: '12px',
                        border: scheduleData.isActive ? '1px solid rgba(34, 197, 94, 0.2)' : '1px solid rgba(156, 163, 175, 0.2)',
                        display: 'flex',
                        alignItems: 'center',
                        justifyContent: 'space-between',
                        flexWrap: 'wrap',
                        gap: '1rem'
                      }}>
                        <div>
                          <span style={{ 
                            fontWeight: '600',
                            color: scheduleData.isActive ? '#065f46' : '#374151'
                          }}>
                            <div style={{ display: 'flex', alignItems: 'center', gap: '0.5rem' }}>
                            Status:
                            {scheduleData.isActive ? (
                              <>
                                <svg width="12" height="12" viewBox="0 0 24 24" fill="currentColor" style={{ color: '#22c55e' }}>
                                  <circle cx="12" cy="12" r="12"/>
                                </svg>
                                Active
                              </>
                            ) : (
                              <>
                                <svg width="12" height="12" viewBox="0 0 24 24" fill="currentColor" style={{ color: '#6b7280' }}>
                                  <circle cx="12" cy="12" r="12"/>
                                </svg>
                                Inactive
                              </>
                            )}
                          </div>
                          </span>
                          {scheduleData.isActive && scheduleData.time && (
                            <p style={{ 
                              margin: '0.25rem 0 0 0',
                              fontSize: '0.875rem',
                              color: '#6b7280'
                            }}>
                              Next post: {scheduleData.frequency} at {scheduleData.time}
                            </p>
                          )}
                        </div>
                        <button
                          onClick={() => setScheduleData(prev => ({ ...prev, isActive: !prev.isActive }))}
                          disabled={!scheduleData.prompt || !scheduleData.time}
                          style={{
                            padding: '0.5rem 1rem',
                            background: scheduleData.isActive ? 'rgba(239, 68, 68, 0.8)' : 'rgba(34, 197, 94, 0.8)',
                            color: 'white',
                            border: 'none',
                            borderRadius: '8px',
                            cursor: (!scheduleData.prompt || !scheduleData.time) ? 'not-allowed' : 'pointer',
                            fontWeight: '600',
                            fontSize: '0.875rem',
                            transition: 'all 0.3s ease',
                            opacity: (!scheduleData.prompt || !scheduleData.time) ? 0.5 : 1
                          }}
                        >
                          <div style={{ display: 'flex', alignItems: 'center', gap: '0.5rem' }}>
                            {scheduleData.isActive ? (
                              <>
                                <svg width="16" height="16" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2">
                                  <rect x="6" y="4" width="4" height="16"/>
                                  <rect x="14" y="4" width="4" height="16"/>
                                </svg>
                                Pause Schedule
                              </>
                            ) : (
                              <>
                                <svg width="16" height="16" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2">
                                  <polygon points="8,5 8,19 19,12"/>
                                </svg>
                                Start Schedule
                              </>
                            )}
                          </div>
                        </button>
                      </div>
                    </div>
                  ) : (
                    /* Manual Mode */
                    <div>
                      <label style={{ 
                        display: 'block', 
                        marginBottom: '0.5rem', 
                        fontWeight: '600',
                        color: '#4a5568',
                        fontSize: '0.875rem'
                      }}>
                        üìù Post Content
                      </label>
                      <textarea
                        value={manualFormData.message}
                        onChange={handleManualInputChange}
                        name="message"
                        placeholder="Write your post content here..."
                        style={{
                          width: '100%',
                          minHeight: '140px',
                          padding: '1.25rem',
                          borderRadius: '15px',
                          border: '2px solid rgba(226, 232, 240, 0.8)',
                          background: 'rgba(255, 255, 255, 0.9)',
                          color: '#1a202c',
                          resize: 'vertical',
                          fontSize: '1rem',
                          transition: 'all 0.3s ease',
                          fontFamily: 'inherit'
                        }}
                      />
                    </div>
                  )}
                </div>

                {/* Media Options - Only show for auto and manual modes */}
                {activeTab !== 'schedule' && (
                <div style={{ 
                  display: 'flex',
                  gap: '1rem',
                  marginBottom: '2rem',
                  flexWrap: 'wrap'
                }}>
                  {['none', 'photo'].map(type => (
                    <button
                      key={type}
                      onClick={() => handleMediaTypeChange(type, activeTab)}
                      style={{
                        padding: '0.875rem 1.25rem',
                        background: (activeTab === 'auto' ? autoFormData.mediaType : manualFormData.mediaType) === type ?
                          'rgba(16, 185, 129, 0.8)' :
                          'rgba(248, 250, 252, 0.8)',
                        border: 'none',
                        borderRadius: '15px',
                        cursor: 'pointer',
                        display: 'flex',
                        alignItems: 'center',
                        gap: '0.5rem',
                        color: (activeTab === 'auto' ? autoFormData.mediaType : manualFormData.mediaType) === type ? 'white' : '#4a5568',
                        transition: 'all 0.3s ease',
                        fontWeight: '600',
                        backdropFilter: 'blur(10px)',
                        WebkitBackdropFilter: 'blur(10px)',
                        boxShadow: (activeTab === 'auto' ? autoFormData.mediaType : manualFormData.mediaType) === type ?
                          '0 8px 25px rgba(16, 185, 129, 0.4)' :
                          '0 4px 15px rgba(0, 0, 0, 0.1)'
                      }}
                    >
                      <MediaIcon type={type} />
                      {type === 'none' ? 'Text Only' : type.charAt(0).toUpperCase() + type.slice(1)}
                    </button>
                  ))}
                </div>
                )}

                {/* Action Buttons - Only show for auto and manual modes */}
                {activeTab !== 'schedule' && (
                <div style={{ 
                  display: 'flex',
                  gap: '1rem',
                  justifyContent: 'flex-end',
                  flexWrap: 'wrap'
                }}>
                  {activeTab === 'auto' && (
                    <button
                      onClick={generatePostContent}
                      disabled={autoFormData.isGenerating || !autoFormData.prompt.trim()}
                      style={{
                        padding: '1rem 2rem',
                        background: autoFormData.isGenerating ? 
                          'rgba(16, 185, 129, 0.6)' : 
                          'linear-gradient(135deg, #10b981, #059669)',
                        color: 'white',
                        border: 'none',
                        borderRadius: '15px',
                        cursor: 'pointer',
                        opacity: autoFormData.isGenerating || !autoFormData.prompt.trim() ? 0.6 : 1,
                        transition: 'all 0.3s ease',
                        fontWeight: '600',
                        fontSize: '1rem',
                        boxShadow: '0 8px 25px rgba(16, 185, 129, 0.4)',
                        transform: autoFormData.isGenerating ? 'scale(0.95)' : 'scale(1)'
                      }}
                    >
                      <div style={{ display: 'flex', alignItems: 'center', gap: '0.5rem' }}>
                        {autoFormData.isGenerating ? (
                          <>
                            <svg width="16" height="16" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" style={{ animation: 'spin 1s linear infinite' }}>
                              <path d="M21 12a9 9 0 11-6.219-8.56"/>
                            </svg>
                            Generating...
                          </>
                        ) : (
                          <>
                            <svg width="16" height="16" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2">
                              <circle cx="12" cy="12" r="3"/>
                              <path d="M12 1v6m0 6v6m11-7h-6m-6 0H1"/>
                            </svg>
                            Generate Content
                          </>
                        )}
                      </div>
                    </button>
                  )}

                  <button
                    onClick={publishPost}
                    disabled={isPublishing || (activeTab === 'auto' ? !autoFormData.generatedContent : !manualFormData.message)}
                    style={{
                      padding: '1rem 2rem',
                      background: isPublishing ? 
                        'rgba(59, 130, 246, 0.6)' : 
                        'linear-gradient(135deg, #3b82f6, #2563eb)',
                      color: 'white',
                      border: 'none',
                      borderRadius: '15px',
                      cursor: 'pointer',
                      opacity: isPublishing || (activeTab === 'auto' ? !autoFormData.generatedContent : !manualFormData.message) ? 0.6 : 1,
                      transition: 'all 0.3s ease',
                      fontWeight: '600',
                      fontSize: '1rem',
                      boxShadow: '0 8px 25px rgba(59, 130, 246, 0.4)',
                      transform: isPublishing ? 'scale(0.95)' : 'scale(1)'
                    }}
                  >
                    <div style={{ display: 'flex', alignItems: 'center', gap: '0.5rem' }}>
                      {isPublishing ? (
                        <>
                          <svg width="16" height="16" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" style={{ animation: 'spin 1s linear infinite' }}>
                            <path d="M21 12a9 9 0 11-6.219-8.56"/>
                          </svg>
                          Publishing...
                        </>
                      ) : (
                        <>
                          <svg width="16" height="16" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2">
                            <path d="M12 19V5m-7 7l7-7 7 7"/>
                          </svg>
                          Publish Post
                        </>
                      )}
                    </div>
                  </button>
                </div>
                )}
              </div>
            )}

            {/* Posts History Section */}
            {selectedPage && facebookConnected && (
              <div className="glass-effect" style={{ 
                marginTop: '3rem',
                padding: '2rem',
                borderRadius: '25px',
                background: 'rgba(248, 250, 252, 0.8)',
                backdropFilter: 'blur(20px)',
                WebkitBackdropFilter: 'blur(20px)',
                border: '1px solid rgba(226, 232, 240, 0.8)',
                boxShadow: '0 15px 30px rgba(0, 0, 0, 0.05)',
                position: 'relative',
                zIndex: 1,
                maxWidth: '1000px',
                width: '100%'
              }}>
                {/* Section Header */}
                <div style={{ 
                  display: 'flex',
                  alignItems: 'center',
                  justifyContent: 'space-between',
                  marginBottom: '2rem'
                }}>
                  <div style={{ display: 'flex', alignItems: 'center', gap: '0.75rem' }}>
                    <div style={{
                      padding: '0.75rem',
                      borderRadius: '12px',
                      background: 'rgba(59, 130, 246, 0.1)',
                      display: 'flex',
                      alignItems: 'center',
                      justifyContent: 'center'
                    }}>
                      <svg width="20" height="20" viewBox="0 0 24 24" fill="none" stroke="#3b82f6" strokeWidth="2">
                        <path d="M19 21l-7-5-7 5V5a2 2 0 0 1 2-2h10a2 2 0 0 1 2 2z"/>
                      </svg>
                    </div>
                    <div>
                      <h3 style={{
                        fontSize: '1.5rem',
                        fontWeight: '700',
                        color: '#1a202c',
                        margin: '0'
                      }}>
                        Recent Posts
                      </h3>
                      <p style={{
                        fontSize: '0.875rem',
                        color: '#6b7280',
                        margin: '0'
                      }}>
                        Your latest Facebook posts from {selectedPage.name}
                      </p>
                    </div>
                  </div>

                  <button
                    onClick={loadPostHistory}
                    disabled={isLoadingPosts}
                    style={{
                      padding: '0.75rem 1.5rem',
                      background: 'rgba(59, 130, 246, 0.1)',
                      border: '1px solid rgba(59, 130, 246, 0.2)',
                      borderRadius: '12px',
                      color: '#3b82f6',
                      cursor: 'pointer',
                      display: 'flex',
                      alignItems: 'center',
                      gap: '0.5rem',
                      fontWeight: '600',
                      fontSize: '0.875rem',
                      transition: 'all 0.3s ease',
                      opacity: isLoadingPosts ? 0.7 : 1
                    }}
                  >
                    <svg width="16" height="16" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2">
                      <path d="M23 4v6h-6"/>
                      <path d="M20.49 15a9 9 0 1 1-2.12-9.36L23 10"/>
                    </svg>
                    {isLoadingPosts ? 'Loading...' : 'Refresh'}
                  </button>
                </div>

                {/* Posts List */}
                <div style={{ 
                  display: 'flex',
                  flexDirection: 'column',
                  gap: '1rem'
                }}>
                  {isLoadingPosts ? (
                    <div style={{
                      display: 'flex',
                      alignItems: 'center',
                      justifyContent: 'center',
                      padding: '3rem',
                      color: '#6b7280'
                    }}>
                      <svg width="24" height="24" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" style={{ animation: 'spin 1s linear infinite' }}>
                        <path d="M21 12a9 9 0 11-6.219-8.56"/>
                      </svg>
                      <span style={{ marginLeft: '0.75rem' }}>Loading posts...</span>
                    </div>
                  ) : postHistory.length === 0 ? (
                    <div style={{
                      textAlign: 'center',
                      padding: '3rem',
                      color: '#6b7280'
                    }}>
                      <svg width="48" height="48" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="1" style={{ margin: '0 auto 1rem auto' }}>
                        <circle cx="12" cy="12" r="10"/>
                        <path d="M8 14s1.5 2 4 2 4-2 4-2"/>
                        <line x1="9" y1="9" x2="9.01" y2="9"/>
                        <line x1="15" y1="9" x2="15.01" y2="9"/>
                      </svg>
                      <p style={{ margin: '0', fontSize: '1.1rem', fontWeight: '600' }}>No posts yet</p>
                      <p style={{ margin: '0.5rem 0 0 0', fontSize: '0.875rem' }}>Your published posts will appear here</p>
                    </div>
                  ) : (
                    postHistory.map((post, index) => (
                      <div
                        key={post.id || index}
                        style={{
                          padding: '1.5rem',
                          background: 'rgba(255, 255, 255, 0.8)',
                          borderRadius: '15px',
                          border: '1px solid rgba(226, 232, 240, 0.8)',
                          transition: 'all 0.3s ease'
                        }}
                      >
                        <div style={{ display: 'flex', alignItems: 'flex-start', gap: '1rem' }}>
                          <div style={{
                            width: '40px',
                            height: '40px',
                            borderRadius: '50%',
                            background: 'rgba(24, 119, 242, 0.2)',
                            display: 'flex',
                            alignItems: 'center',
                            justifyContent: 'center',
                            flexShrink: 0
                          }}>
                            <svg width="20" height="20" viewBox="0 0 24 24" fill="#1877f2">
                              <path d="M24 12.073c0-6.627-5.373-12-12-12s-12 5.373-12 12c0 5.99 4.388 10.954 10.125 11.854v-8.385H7.078v-3.47h3.047V9.43c0-3.007 1.792-4.669 4.533-4.669 1.312 0 2.686.235 2.686.235v2.953H15.83c-1.491 0-1.956.925-1.956 1.874v2.25h3.328l-.532 3.47h-2.796v8.385C19.612 23.027 24 18.062 24 12.073z"/>
                            </svg>
                          </div>
                          <div style={{ flex: 1 }}>
                            <div style={{ display: 'flex', alignItems: 'center', gap: '0.5rem', marginBottom: '0.5rem' }}>
                              <span style={{ fontWeight: '600', color: '#1a202c' }}>{selectedPage.name}</span>
                              <span style={{ fontSize: '0.75rem', color: '#6b7280' }}>
                                {post.created_at ? new Date(post.created_at).toLocaleDateString() : 'Recently'}
                              </span>
                              <div style={{
                                padding: '0.25rem 0.5rem',
                                background: post.status === 'published' ? 'rgba(34, 197, 94, 0.1)' : 'rgba(156, 163, 175, 0.1)',
                                color: post.status === 'published' ? '#065f46' : '#374151',
                                borderRadius: '6px',
                                fontSize: '0.75rem',
                                fontWeight: '600'
                              }}>
                                {post.status || 'Published'}
                              </div>
                            </div>
                            <p style={{ 
                              margin: '0',
                              color: '#374151',
                              lineHeight: '1.5',
                              fontSize: '0.95rem'
                            }}>
                              {post.content || post.message || 'Post content not available'}
                            </p>
                          </div>
                        </div>
                      </div>
                    ))
                  )}
                </div>
              </div>
            )}
          </div>
        )}
      </div>

      <style jsx>{`
        @keyframes spin {
          from { transform: rotate(0deg); }
          to { transform: rotate(360deg); }
        }

        @keyframes pulse {
          0%, 100% {
            opacity: 1;
            transform: scale(1);
          }
          50% {
            opacity: 0.7;
            transform: scale(1.2);
          }
        }
      `}</style>
             </div>
    </div>
    </>
  );
}

export default FacebookPage;
