import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import ActivityLog from './ActivityLog';
import PlatformDetails from './PlatformDetails';
import './SocialMediaPage.css';
import { createSocialPlatforms } from '../constants/socialPlatforms';

// Social Media Platform Card Component (moved from PlatformCard.js)
const SocialMediaPlatformCard = ({ 
  platform, 
  isSelected, 
  isFlipped, 
  onSelect, 
  onConnect, 
  onFlip, 
  postData, 
  setPostData 
}) => {
  const handleCardClick = () => {
    if (platform.id !== 'facebook') {
      onSelect(isSelected ? null : platform.id);
    }
  };

  const handleConnectClick = (e) => {
    e.stopPropagation();
    if (platform.id === 'facebook') {
      onFlip();
    } else {
      onConnect(platform.id);
    }
  };

  return (
    <div
      className={`platform-card ${isSelected ? 'selected' : ''} ${platform.id === 'facebook' && isFlipped ? 'flipped' : ''}`}
      onClick={handleCardClick}
      style={{
        '--platform-color': platform.color,
        '--platform-gradient': platform.gradient
      }}
    >
      <div className="card-inner">
        <div className="card-front">
          <div className="platform-status">
            <span className={`status-badge ${platform.status.toLowerCase().replace(' ', '-')}`}>
              {platform.status}
            </span>
          </div>

          <div className="platform-header">
            <div className="platform-icon-wrapper">
              <div className="platform-icon" style={{color: platform.color}}>
                {platform.icon}
              </div>
            </div>
            <div className="platform-info">
              <h3 className="platform-name">{platform.name}</h3>
              <p className="platform-description">{platform.description}</p>
            </div>
          </div>

          <div className="platform-stats">
            <div className="stat-item">
              <span className="stat-number" style={{color: platform.color}}>{platform.stats.posts}</span>
              <span className="stat-label">Posts</span>
            </div>
            <div className="stat-item">
              <span className="stat-number" style={{color: platform.color}}>{platform.stats.replies}</span>
              <span className="stat-label">Replies</span>
            </div>
            <div className="stat-item">
              <span className="stat-number" style={{color: platform.color}}>{platform.stats.messages}</span>
              <span className="stat-label">Messages</span>
            </div>
          </div>

          <div className="platform-features">
            <h4>Automation Features:</h4>
            <div className="features-list">
              {platform.features.map((feature, index) => (
                <div key={index} className="feature-item">
                  <span>{feature}</span>
                </div>
              ))}
            </div>
          </div>

          <div className="platform-actions">
            {platform.id === 'facebook' ? (
              platform.connected ? (
                <button 
                  className="connected-btn"
                  disabled
                >
                  <span>âœ“ Connected</span>
                </button>
              ) : (
                <button 
                  className="connect-btn" 
                  style={{background: platform.gradient}}
                  onClick={handleConnectClick}
                >
                  <span>Continue with Facebook</span>
                  <svg width="16" height="16" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2">
                    <path d="M7 17l9.2-9.2M17 17V7H7"/>
                  </svg>
                </button>
              )
            ) : (
              <button className="connect-btn" style={{background: platform.gradient}}>
                <span>Connect {platform.name}</span>
                <svg width="16" height="16" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2">
                  <path d="M7 17l9.2-9.2M17 17V7H7"/>
                </svg>
              </button>
            )}
          </div>
        </div>

        {platform.id === 'facebook' && (
          <FacebookConnectionForm
            onBack={() => onFlip()}
            onConnect={onConnect}
            postData={postData}
            setPostData={setPostData}
          />
        )}
      </div>
    </div>
  );
};

// Facebook Connection Form Component (moved from PlatformCard.js)
const FacebookConnectionForm = ({ onBack, onConnect, postData, setPostData }) => (
  <div className="card-back">
    <div className="connection-form">
      <div className="form-header">
        <button className="back-to-card" onClick={onBack}>
          <svg width="20" height="20" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2">
            <polyline points="15,18 9,12 15,6"></polyline>
          </svg>
        </button>
        <h3>Connect to Facebook</h3>
      </div>
      
      <button 
        className="oauth-connect-btn" 
        style={{background: "linear-gradient(135deg, #1877F2 0%, #42a5f5 100%)"}}
        onClick={onConnect}
      >
        <span>Connect to Facebook</span>
        <svg width="16" height="16" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2">
          <path d="M7 17l9.2-9.2M17 17V7H7"/>
        </svg>
      </button>
      
      <div className="message-input-section">
        <label htmlFor="facebook-message">Message to send to Make.com:</label>
        <textarea
          id="facebook-message"
          placeholder="Enter your message here..."
          value={postData.message}
          onChange={(e) => setPostData(prev => ({...prev, message: e.target.value}))}
          rows="3"
          className="message-textarea"
        />
      </div>
      
      <div className="toggles-container">
        <div className="auto-reply-toggle">
          <label className="toggle-label">
            <span>Enable Auto Reply to Comments</span>
            <label className="toggle">
              <input 
                type="checkbox" 
                checked={postData.autoReplyComment}
                onChange={(e) => setPostData(prev => ({...prev, autoReplyComment: e.target.checked}))}
              />
              <span className="toggle-slider"></span>
            </label>
          </label>
        </div>
        
        <div className="auto-reply-toggle">
          <label className="toggle-label">
            <span>Enable Auto DM Response</span>
            <label className="toggle">
              <input 
                type="checkbox" 
                checked={postData.autoDMResponse}
                onChange={(e) => setPostData(prev => ({...prev, autoDMResponse: e.target.checked}))}
              />
              <span className="toggle-slider"></span>
            </label>
          </label>
        </div>
      </div>
    </div>
  </div>
);

function SocialMediaPage() {
  const navigate = useNavigate();
  const [selectedPlatform, setSelectedPlatform] = useState(null);
  const [facebookConnected, setFacebookConnected] = useState(false);
  const [postData, setPostData] = useState({
    message: '',
    image: null,
    autoReplyComment: false,
    autoDMResponse: false
  });
  const [activityLog, setActivityLog] = useState([]);
  const [isCardFlipped, setIsCardFlipped] = useState(false);
  const [sdkLoaded, setSdkLoaded] = useState(false);

  // Load Facebook SDK only when needed
  const loadFacebookSDK = () => {
    if (window.FB || sdkLoaded) {
      console.log('Facebook SDK already loaded');
      return Promise.resolve();
    }

    return new Promise((resolve, reject) => {
      console.log('Loading Facebook SDK...');
      setSdkLoaded(true);

      window.fbAsyncInit = function() {
        const appId = process.env.REACT_APP_FACEBOOK_APP_ID || 'your-facebook-app-id';
        
        console.log('Initializing Facebook SDK with App ID:', appId);
        
        if (!appId || appId === 'your-facebook-app-id') {
          console.error('âŒ Facebook App ID not configured! Please set REACT_APP_FACEBOOK_APP_ID in your .env file');
          reject(new Error('Facebook App ID not configured'));
          return;
        }

        window.FB.init({
          appId: appId,
          cookie: true,
          xfbml: true,
          version: 'v18.0'
        });

        // Check if initialization was successful
        window.FB.getLoginStatus((response) => {
          console.log('Facebook SDK initialized. Login status:', response.status);
          resolve();
        });

        console.log('âœ… Facebook SDK initialized successfully');
      };

      // Load Facebook SDK script
      if (!document.getElementById('facebook-jssdk')) {
        console.log('Loading Facebook SDK script...');
        const script = document.createElement('script');
        script.id = 'facebook-jssdk';
        script.src = "https://connect.facebook.net/en_US/sdk.js";
        script.async = true;
        script.onload = () => {
          console.log('âœ… Facebook SDK script loaded');
        };
        script.onerror = () => {
          console.error('âŒ Failed to load Facebook SDK script');
          reject(new Error('Failed to load Facebook SDK script'));
        };
        document.body.appendChild(script);
      }
    });
  };

  const socialPlatforms = createSocialPlatforms(facebookConnected);
  const WEBHOOK_URL = "https://hook.eu2.make.com/m4f3qzhwhe4y62i9ghviwe4zw23hytio";

  const addActivity = (action, status) => {
    setActivityLog(prev => [...prev, {
      time: new Date().toLocaleString(),
      action,
      status
    }]);
  };

  const handleFacebookConnect = () => {
    setIsCardFlipped(true);
  };

  const handleFacebookLogin = async () => {
    try {
      console.log("ðŸš€ handleFacebookLogin triggered");
      
      // Load Facebook SDK first
      addActivity('Loading Facebook SDK...', 'info');
      await loadFacebookSDK();
  
      const testPayload = {
        text: "ðŸš€ Hello from React UI!",
        pageId: "dummy_id",
        token: "dummy_token"
      };
  
      console.log("ðŸ“¡ Sending to Webhook:", WEBHOOK_URL, testPayload);
  
      const webhookResponse = await fetch(WEBHOOK_URL, {
        method: "POST",
        headers: {
          "Content-Type": "application/json"
        },
        body: JSON.stringify(testPayload)
      });
  
      console.log("âœ… Webhook response status:", webhookResponse.status);
  
      const result = await webhookResponse.text();
      console.log("ðŸ“¥ Webhook response body:", result);
  
      if (webhookResponse.ok) {
        setFacebookConnected(true);
        setIsCardFlipped(false);
        addActivity('âœ… Webhook test successful!', 'success');
      } else {
        throw new Error('âŒ Webhook test failed');
      }
    } catch (error) {
      console.error('ðŸ”¥ Webhook test error:', error);
      addActivity('âŒ Webhook test error', 'error');
    }
  };
  
  const handlePlatformConnect = (platformId) => {
    if (platformId === 'facebook') {
      handleFacebookLogin();
    } else {
      // Handle other platform connections
      addActivity(`${platformId} connection initiated`, 'success');
    }
  };

  const selectedPlatformData = socialPlatforms.find(p => p.id === selectedPlatform);

  return (
    <div className="page-container">
      <header className="page-header">
        <button onClick={() => navigate('/')} className="back-btn">
          <svg width="20" height="20" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2">
            <polyline points="15,18 9,12 15,6"></polyline>
          </svg>
          Back to Dashboard
        </button>
        <div className="page-title-section">
          <h1 className="page-title">Social Media Automation</h1>
          <p className="page-subtitle">Connect and automate your social media platforms with advanced AI-powered features</p>
        </div>
      </header>

      <main className="page-main">
        <div className="platforms-container">
          <div className="platforms-grid">
            {socialPlatforms.map((platform) => (
              <SocialMediaPlatformCard
                key={platform.id}
                platform={platform}
                isSelected={selectedPlatform === platform.id}
                isFlipped={platform.id === 'facebook' && isCardFlipped}
                onSelect={setSelectedPlatform}
                onConnect={handlePlatformConnect}
                onFlip={handleFacebookConnect}
                postData={postData}
                setPostData={setPostData}
              />
            ))}
          </div>
        </div>

        <ActivityLog activityLog={activityLog} />

        {selectedPlatform && selectedPlatformData && (
          <PlatformDetails
            platform={selectedPlatformData}
            onClose={() => setSelectedPlatform(null)}
          />
        )}
      </main>
    </div>
  );
}

export default SocialMediaPage;
