import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import './SocialMediaPage.css';

function SocialMediaPage() {
  const navigate = useNavigate();
  const [isConnecting, setIsConnecting] = useState(false);
  const [connectionStatus, setConnectionStatus] = useState('');
  const [hoveredCard, setHoveredCard] = useState(null);
  
  // Facebook OAuth states
  const [pageAccessToken, setPageAccessToken] = useState('');
  const [pages, setPages] = useState([]);
  const [selectedPage, setSelectedPage] = useState(null);
  const [isAuthenticated, setIsAuthenticated] = useState(false);
  const [isDevelopmentMode, setIsDevelopmentMode] = useState(false);
  
  // Form data state - simplified to only include message
  const [formData, setFormData] = useState({
    message: ''
  });

  const WEBHOOK_URL = "https://hook.eu2.make.com/m4f3qzhwhe4y62i9ghviwe4zw23hytio";

  // Check if we're in development mode (HTTP)
  const isHttpDevelopment = window.location.protocol === 'http:' && 
                            (window.location.hostname === 'localhost' || window.location.hostname === '127.0.0.1');

  // Initialize Facebook SDK
  useEffect(() => {
    // Skip Facebook SDK if in HTTP development mode
    if (isHttpDevelopment) {
      setIsDevelopmentMode(true);
      setConnectionStatus('ðŸš§ Development Mode: Facebook OAuth disabled (requires HTTPS)');
      return;
    }

    // Load Facebook SDK
    const loadFacebookSDK = () => {
      return new Promise((resolve) => {
        if (window.FB) {
          resolve();
          return;
        }

        window.fbAsyncInit = function () {
          window.FB.init({
            appId: '611792801934261',
            cookie: true,
            xfbml: true,
            version: 'v20.0' // Updated to latest stable version
          });
          
          console.log('âœ… Facebook SDK initialized');
          
          // Check if user is already logged in
          window.FB.getLoginStatus((response) => {
            console.log('Facebook login status:', response);
            if (response.status === 'connected') {
              fetchPages(response.authResponse.accessToken);
            }
          });
          
          resolve();
        };

        // Only load script if not already loaded
        if (!document.getElementById('facebook-jssdk')) {
          const script = document.createElement('script');
          script.id = 'facebook-jssdk';
          script.src = 'https://connect.facebook.net/en_US/sdk.js';
          script.async = true;
          script.defer = true;
          script.crossOrigin = 'anonymous';
          document.body.appendChild(script);
        } else if (window.FB) {
          // SDK already loaded
          resolve();
        }
      });
    };

    loadFacebookSDK().catch(error => {
      console.error('Failed to load Facebook SDK:', error);
      setConnectionStatus('âŒ Failed to load Facebook SDK');
    });
  }, [isHttpDevelopment]);

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));
  };

  const handlePageSelection = (e) => {
    const pageId = e.target.value;
    const page = pages.find(p => p.id === pageId);
    setSelectedPage(page);
    setPageAccessToken(page.access_token);
  };

  // Development mode simulation
  const simulateAuthentication = () => {
    setIsConnecting(true);
    setConnectionStatus('ðŸ”§ Simulating Facebook connection...');
    
    setTimeout(() => {
      // Simulate successful authentication
      const mockPage = {
        id: 'dev_page_123',
        name: 'Development Test Page',
        access_token: 'dev_token_123'
      };
      
      setPages([mockPage]);
      setSelectedPage(mockPage);
      setPageAccessToken(mockPage.access_token);
      setIsAuthenticated(true);
      setConnectionStatus('âœ… Development Mode: Ready to test webhook!');
      setIsConnecting(false);
    }, 2000);
  };

  const loginWithFacebook = () => {
    // Check if Facebook SDK is loaded
    if (!window.FB) {
      setConnectionStatus('âŒ Facebook SDK not loaded. Please refresh the page.');
      return;
    }

    // Check if running on HTTPS
    if (window.location.protocol !== 'https:' && window.location.hostname !== 'localhost') {
      setConnectionStatus('âŒ Facebook login requires HTTPS. Please use https://localhost:3000');
      return;
    }

    setIsConnecting(true);
    setConnectionStatus('ðŸ” Connecting to Facebook...');
    
    try {
      window.FB.login((response) => {
        console.log('Facebook login response:', response);
        
        if (response.authResponse) {
          setConnectionStatus('âœ… Facebook login successful!');
          fetchPages(response.authResponse.accessToken);
        } else if (response.status === 'not_authorized') {
          setConnectionStatus('âŒ Please authorize the app to continue');
          setIsConnecting(false);
        } else {
          setConnectionStatus('âŒ Facebook login cancelled');
          setIsConnecting(false);
        }
      }, {
        scope: 'pages_show_list,pages_read_engagement,pages_manage_posts,pages_manage_metadata'
      });
    } catch (error) {
      console.error('Facebook login error:', error);
      
      // Handle specific HTTPS error
      if (error.message && error.message.includes('http pages')) {
        setConnectionStatus('âŒ Facebook requires HTTPS. Please restart with: npm run start (HTTPS enabled)');
      } else {
        setConnectionStatus('âŒ Facebook login failed: ' + error.message);
      }
      setIsConnecting(false);
    }
  };

  const fetchPages = (userAccessToken) => {
    setConnectionStatus('ðŸ“„ Fetching your Facebook pages...');
    
    try {
      window.FB.api('/me/accounts', 'GET', { access_token: userAccessToken }, (response) => {
        console.log('Facebook pages response:', response);
        
        if (response.error) {
          console.error('Facebook API error:', response.error);
          setConnectionStatus(`âŒ Facebook API error: ${response.error.message}`);
          setIsConnecting(false);
          return;
        }
        
        if (response.data && response.data.length > 0) {
          setPages(response.data);
          const firstPage = response.data[0];
          setSelectedPage(firstPage);
          setPageAccessToken(firstPage.access_token);
          setIsAuthenticated(true);
          setConnectionStatus(`âœ… Connected! Found ${response.data.length} page(s)`);
        } else {
          setConnectionStatus('âŒ No Facebook pages found. You need to be an admin of a Facebook page.');
        }
        setIsConnecting(false);
      });
    } catch (error) {
      console.error('Fetch pages error:', error);
      setConnectionStatus('âŒ Failed to fetch Facebook pages');
      setIsConnecting(false);
    }
  };

  const handleFacebookConnect = async () => {
    // Development mode bypass
    if (isDevelopmentMode && !isAuthenticated) {
      simulateAuthentication();
      return;
    }

    if (!isAuthenticated && !isDevelopmentMode) {
      loginWithFacebook();
      return;
    }

    // Validate required fields - message and page selection
    if (!formData.message || formData.message.trim() === '') {
      setConnectionStatus('âŒ Please enter a message');
      return;
    }

    if (!selectedPage || !pageAccessToken) {
      setConnectionStatus('âŒ Please select a Facebook page');
      return;
    }

    try {
      setIsConnecting(true);
      setConnectionStatus('ðŸš€ Posting to Facebook...');
      console.log("ðŸš€ Sending Facebook post via webhook");
      
      const payload = {
        pageAccessToken,
        pageId: selectedPage.id,
        pageName: selectedPage.name,
        message: formData.message,
        timestamp: new Date().toISOString(),
        platform: 'facebook',
        authenticated: true,
        developmentMode: isDevelopmentMode
      };
  
      console.log("ðŸ“¡ Sending to Webhook:", WEBHOOK_URL, payload);
  
      const webhookResponse = await fetch(WEBHOOK_URL, {
        method: "POST",
        headers: {
          "Content-Type": "application/json"
        },
        body: JSON.stringify(payload)
      });
  
      console.log("âœ… Webhook response status:", webhookResponse.status);
      const result = await webhookResponse.text();
      console.log("ðŸ“¥ Webhook response body:", result);
  
      if (webhookResponse.ok) {
        setConnectionStatus(isDevelopmentMode ? 
          'âœ… Test message sent to webhook successfully!' : 
          'âœ… Message posted to Facebook successfully!');
        // Reset form after successful submission
        setFormData({
          message: ''
        });
      } else {
        setConnectionStatus('âŒ Failed to post to Facebook');
      }
    } catch (error) {
      console.error('ðŸ”¥ Webhook error:', error);
      setConnectionStatus('âŒ Connection error occurred');
    } finally {
      setIsConnecting(false);
    }
  };

  const handleInstagramConnect = () => {
    // Placeholder for Instagram logic
    console.log("Instagram connect clicked - logic to be implemented");
    setConnectionStatus('ðŸš§ Instagram integration coming soon!');
  };

  const handleLogout = () => {
    if (isDevelopmentMode) {
      setIsAuthenticated(false);
      setPages([]);
      setSelectedPage(null);
      setPageAccessToken('');
      setConnectionStatus('ðŸ‘‹ Logged out from development mode');
    } else {
      window.FB.logout(() => {
        setIsAuthenticated(false);
        setPages([]);
        setSelectedPage(null);
        setPageAccessToken('');
        setConnectionStatus('ðŸ‘‹ Logged out from Facebook');
      });
    }
  };

  const platforms = [
    {
      id: 'facebook',
      name: 'Facebook',
      description: isAuthenticated ? 
        `Connected as ${selectedPage?.name || 'Facebook User'}${isDevelopmentMode ? ' (Dev Mode)' : ''}` : 
        (isDevelopmentMode ? 'Development Mode - Test Webhook' : 'Connect with Facebook OAuth'),
      hasForm: true,
      icon: (
        <svg width="64" height="64" viewBox="0 0 24 24" fill="#1877F2">
          <path d="M24 12.073c0-6.627-5.373-12-12-12s-12 5.373-12 12c0 5.99 4.388 10.954 10.125 11.854v-8.385H7.078v-3.47h3.047V9.43c0-3.007 1.792-4.669 4.533-4.669 1.312 0 2.686.235 2.686.235v2.953H15.83c-1.491 0-1.956.925-1.956 1.874v2.25h3.328l-.532 3.47h-2.796v8.385C19.612 23.027 24 18.062 24 12.073z"/>
        </svg>
      ),
      connectHandler: handleFacebookConnect,
      isConnecting: isConnecting,
      gradient: 'linear-gradient(135deg, #1877F2 0%, #42a5f5 100%)',
      borderColor: '#1877F2'
    },
    {
      id: 'instagram',
      name: 'Instagram',
      description: 'Automate your Instagram marketing campaigns',
      hasForm: false,
      icon: (
        <svg width="64" height="64" viewBox="0 0 24 24" fill="url(#instagram-gradient)">
          <defs>
            <linearGradient id="instagram-gradient" x1="0%" y1="0%" x2="100%" y2="100%">
              <stop offset="0%" stopColor="#405DE6"/>
              <stop offset="25%" stopColor="#5851DB"/>
              <stop offset="50%" stopColor="#833AB4"/>
              <stop offset="75%" stopColor="#C13584"/>
              <stop offset="100%" stopColor="#E1306C"/>
            </linearGradient>
          </defs>
          <path d="M12 2.163c3.204 0 3.584.012 4.85.07 3.252.148 4.771 1.691 4.919 4.919.058 1.265.069 1.645.069 4.849 0 3.205-.012 3.584-.069 4.849-.149 3.225-1.664 4.771-4.919 4.919-1.266.058-1.644.07-4.85.07-3.204 0-3.584-.012-4.849-.07-3.26-.149-4.771-1.699-4.919-4.92-.058-1.265-.07-1.644-.07-4.849 0-3.204.013-3.583.07-4.849.149-3.227 1.664-4.771 4.919-4.919 1.266-.057 1.645-.069 4.849-.069zm0-2.163c-3.259 0-3.667.014-4.947.072-4.358.2-6.78 2.618-6.98 6.98-.059 1.281-.073 1.689-.073 4.948 0 3.259.014 3.668.072 4.948.2 4.358 2.618 6.78 6.98 6.98 1.281.058 1.689.072 4.948.072 3.259 0 3.668-.014 4.948-.072 4.354-.2 6.782-2.618 6.979-6.98.059-1.28.073-1.689.073-4.948 0-3.259-.014-3.667-.072-4.947-.196-4.354-2.617-6.78-6.979-6.98-1.281-.059-1.69-.073-4.949-.073zm0 5.838c-3.403 0-6.162 2.759-6.162 6.162s2.759 6.163 6.162 6.163 6.162-2.759 6.162-6.163c0-3.403-2.759-6.162-6.162-6.162zm0 10.162c-2.209 0-4-1.79-4-4 0-2.209 1.791-4 4-4s4 1.791 4 4c0 2.21-1.791 4-4 4zm6.406-11.845c-.796 0-1.441.645-1.441 1.44s.645 1.44 1.441 1.44c.795 0 1.439-.645 1.439-1.44s-.644-1.44-1.439-1.44z"/>
        </svg>
      ),
      connectHandler: handleInstagramConnect,
      isConnecting: false,
      gradient: 'linear-gradient(135deg, #405DE6 0%, #5851DB 25%, #833AB4 50%, #C13584 75%, #E1306C 100%)',
      borderColor: '#E1306C'
    }
  ];

  return (
    <div className="page-container">
      <header className="page-header">
        <button onClick={() => navigate('/')} className="back-btn">
          <svg width="20" height="20" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2">
            <polyline points="15,18 9,12 15,6"></polyline>
          </svg>
          Back to Dashboard
        </button>
        <div className="page-title-section">
          <h1 className="page-title">Social Media Automation</h1>
          <p className="page-subtitle">
            Connect and manage your social media platforms
            {isDevelopmentMode && <span className="dev-badge"> ðŸ”§ DEV MODE</span>}
          </p>
        </div>
      </header>

      <main className="page-main">
        <div className="platforms-container">
          {isDevelopmentMode && (
            <div className="dev-notice">
              <h3>ðŸš§ Development Mode Active</h3>
              <p>Facebook OAuth requires HTTPS. You can:</p>
              <ul>
                <li><strong>Test webhook:</strong> Use development mode below</li>
                <li><strong>Enable HTTPS:</strong> Run <code>npm run start</code> and visit <code>https://localhost:3000</code></li>
              </ul>
            </div>
          )}
          
          <div className="platforms-grid">
            {platforms.map((platform) => (
              <div
                key={platform.id}
                className={`platform-card ${hoveredCard === platform.id ? 'hovered' : ''} ${platform.id === 'facebook' && isAuthenticated ? 'authenticated' : ''} ${isDevelopmentMode ? 'dev-mode' : ''}`}
                onMouseEnter={() => setHoveredCard(platform.id)}
                onMouseLeave={() => setHoveredCard(null)}
                style={{
                  '--platform-gradient': platform.gradient,
                  '--platform-border': platform.borderColor
                }}
              >
                <div className="platform-icon">
                  {platform.icon}
                </div>
                
                <div className="platform-content">
                  <h3 className="platform-name">{platform.name}</h3>
                  <p className="platform-description">{platform.description}</p>
                  
                  {platform.hasForm && (isAuthenticated || isDevelopmentMode) ? (
                    <div className="platform-form">
                      {pages.length > 1 && (
                        <div className="form-group">
                          <label className="form-label">
                            ðŸ“„ Select Facebook Page
                          </label>
                          <select
                            value={selectedPage?.id || ''}
                            onChange={handlePageSelection}
                            className="glass-input glass-select"
                          >
                            {pages.map(page => (
                              <option key={page.id} value={page.id}>
                                {page.name}
                              </option>
                            ))}
                          </select>
                        </div>
                      )}
                      <div className="form-group">
                        <label className="form-label">
                          âœ¨ Your Message
                        </label>
                        <textarea
                          name="message"
                          placeholder="What's on your mind? Share your thoughts with the world..."
                          value={formData.message}
                          onChange={handleInputChange}
                          className="glass-input glass-textarea enhanced-textarea"
                          rows="6"
                        />
                      </div>
                    </div>
                  ) : platform.hasForm ? (
                    <div className="auth-prompt">
                      <p className="auth-description">
                        {isDevelopmentMode ? 
                          'ðŸ”§ Click below to test webhook functionality' : 
                          'ðŸ” Please authenticate with Facebook to start posting'}
                      </p>
                    </div>
                  ) : null}
                </div>

                <div className="platform-actions">
                  {platform.id === 'facebook' && (isAuthenticated || isDevelopmentMode) ? (
                    <div className="auth-actions">
                      <button 
                        className="connect-btn"
                        onClick={platform.connectHandler}
                        disabled={platform.isConnecting}
                      >
                        {platform.isConnecting ? (
                          <>
                            <div className="loading-spinner" />
                            {isDevelopmentMode ? 'Testing...' : 'Posting...'}
                          </>
                        ) : (
                          <>
                            {isDevelopmentMode ? 'ðŸ”§ Test Webhook' : 'ðŸš€ Post to Facebook'}
                            <svg width="16" height="16" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2">
                              <polyline points="9,18 15,12 9,6"></polyline>
                            </svg>
                          </>
                        )}
                      </button>
                      <button 
                        className="logout-btn"
                        onClick={handleLogout}
                      >
                        ðŸ‘‹ {isDevelopmentMode ? 'Reset' : 'Logout'}
                      </button>
                    </div>
                  ) : (
                    <button 
                      className="connect-btn"
                      onClick={platform.connectHandler}
                      disabled={platform.isConnecting}
                    >
                      {platform.isConnecting ? (
                        <>
                          <div className="loading-spinner" />
                          Connecting...
                        </>
                      ) : (
                        <>
                          {platform.hasForm ? 
                            (isDevelopmentMode ? 'ðŸ”§ Start Dev Mode' : 'ðŸ” Connect Facebook') : 
                            'ðŸ”— Connect'}
                          <svg width="16" height="16" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2">
                            <polyline points="9,18 15,12 9,6"></polyline>
                          </svg>
                        </>
                      )}
                    </button>
                  )}
                </div>
              </div>
            ))}
          </div>
          
          {connectionStatus && (
            <div className="connection-status">
              <div className={`status-message ${connectionStatus.includes('âœ…') ? 'success' : connectionStatus.includes('ðŸš§') ? 'info' : 'error'}`}>
                {connectionStatus}
              </div>
            </div>
          )}
        </div>
      </main>
    </div>
  );
}

export default SocialMediaPage;
