import React from 'react';

const PlatformCard = ({ 
  platform, 
  isSelected, 
  isFlipped, 
  onSelect, 
  onConnect, 
  onFlip, 
  postData, 
  setPostData 
}) => {
  const handleCardClick = () => {
    if (platform.id !== 'facebook') {
      onSelect(isSelected ? null : platform.id);
    }
  };

  const handleConnectClick = (e) => {
    e.stopPropagation();
    if (platform.id === 'facebook') {
      onFlip();
    } else {
      onConnect(platform.id);
    }
  };

  return (
    <div
      className={`platform-card ${isSelected ? 'selected' : ''} ${platform.id === 'facebook' && isFlipped ? 'flipped' : ''}`}
      onClick={handleCardClick}
      style={{
        '--platform-color': platform.color,
        '--platform-gradient': platform.gradient
      }}
    >
      <div className="card-inner">
        <div className="card-front">
          <div className="platform-status">
            <span className={`status-badge ${platform.status.toLowerCase().replace(' ', '-')}`}>
              {platform.status}
            </span>
          </div>

          <div className="platform-header">
            <div className="platform-icon-wrapper">
              <div className="platform-icon" style={{color: platform.color}}>
                {platform.icon}
              </div>
            </div>
            <div className="platform-info">
              <h3 className="platform-name">{platform.name}</h3>
              <p className="platform-description">{platform.description}</p>
            </div>
          </div>

          <div className="platform-stats">
            <div className="stat-item">
              <span className="stat-number" style={{color: platform.color}}>{platform.stats.posts}</span>
              <span className="stat-label">Posts</span>
            </div>
            <div className="stat-item">
              <span className="stat-number" style={{color: platform.color}}>{platform.stats.replies}</span>
              <span className="stat-label">Replies</span>
            </div>
            <div className="stat-item">
              <span className="stat-number" style={{color: platform.color}}>{platform.stats.messages}</span>
              <span className="stat-label">Messages</span>
            </div>
          </div>

          <div className="platform-features">
            <h4>Automation Features:</h4>
            <div className="features-list">
              {platform.features.map((feature, index) => (
                <div key={index} className="feature-item">
                  <span>{feature}</span>
                </div>
              ))}
            </div>
          </div>

          <div className="platform-actions">
            {platform.id === 'facebook' ? (
              platform.connected ? (
                <button 
                  className="connected-btn"
                  disabled
                >
                  <span>âœ“ Connected</span>
                </button>
              ) : (
                <button 
                  className="connect-btn" 
                  style={{background: platform.gradient}}
                  onClick={handleConnectClick}
                >
                  <span>Continue with Facebook</span>
                  <svg width="16" height="16" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2">
                    <path d="M7 17l9.2-9.2M17 17V7H7"/>
                  </svg>
                </button>
              )
            ) : (
              <button className="connect-btn" style={{background: platform.gradient}}>
                <span>Connect {platform.name}</span>
                <svg width="16" height="16" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2">
                  <path d="M7 17l9.2-9.2M17 17V7H7"/>
                </svg>
              </button>
            )}
          </div>
        </div>

        {platform.id === 'facebook' && (
          <ConnectionForm
            onBack={() => onFlip()}
            onConnect={onConnect}
            postData={postData}
            setPostData={setPostData}
          />
        )}
      </div>
    </div>
  );
};

const ConnectionForm = ({ onBack, onConnect, postData, setPostData }) => (
  <div className="card-back">
    <div className="connection-form">
      <div className="form-header">
        <button className="back-to-card" onClick={onBack}>
          <svg width="20" height="20" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2">
            <polyline points="15,18 9,12 15,6"></polyline>
          </svg>
        </button>
        <h3>Connect to Facebook</h3>
      </div>
      
      <button 
        className="oauth-connect-btn" 
        style={{background: "linear-gradient(135deg, #1877F2 0%, #42a5f5 100%)"}}
        onClick={onConnect}
      >
        <span>Connect to Facebook</span>
        <svg width="16" height="16" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2">
          <path d="M7 17l9.2-9.2M17 17V7H7"/>
        </svg>
      </button>
      
      <div className="message-input-section">
        <label htmlFor="facebook-message">Message to send to Make.com:</label>
        <textarea
          id="facebook-message"
          placeholder="Enter your message here..."
          value={postData.message}
          onChange={(e) => setPostData(prev => ({...prev, message: e.target.value}))}
          rows="3"
          className="message-textarea"
        />
      </div>
      
      <div className="toggles-container">
        <div className="auto-reply-toggle">
          <label className="toggle-label">
            <span>Enable Auto Reply to Comments</span>
            <label className="toggle">
              <input 
                type="checkbox" 
                checked={postData.autoReplyComment}
                onChange={(e) => setPostData(prev => ({...prev, autoReplyComment: e.target.checked}))}
              />
              <span className="toggle-slider"></span>
            </label>
          </label>
        </div>
        
        <div className="auto-reply-toggle">
          <label className="toggle-label">
            <span>Enable Auto DM Response</span>
            <label className="toggle">
              <input 
                type="checkbox" 
                checked={postData.autoDMResponse}
                onChange={(e) => setPostData(prev => ({...prev, autoDMResponse: e.target.checked}))}
              />
              <span className="toggle-slider"></span>
            </label>
          </label>
        </div>
      </div>
    </div>
  </div>
);

export default PlatformCard;
