                <li style={{ display: 'flex', alignItems: 'center', gap: 4 }}>
                  <svg width="16" height="16" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2">
                    <path d="M20 6L9 17l-5-5"/>
                  </svg>
                  Auto-Post Content
                </li>
                <li style={{ display: 'flex', alignItems: 'center', gap: 4 }}>
                  <svg width="16" height="16" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2">
                    <path d="M20 6L9 17l-5-5"/>
                  </svg>
                  AI-Generated Posts
                </li>
                <li style={{ display: 'flex', alignItems: 'center', gap: 4 }}>
                  <svg width="16" height="16" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2">
                    <path d="M20 6L9 17l-5-5"/>
                  </svg>
                  Smart Auto-Replies
                </li>
              </ul>
            </div>
          </div>
        )}
      </div>

      {/* No pages found - simplified version */}
      {facebookConnected && availablePages.length === 0 && (
        <div style={{ 
          marginTop: 24, 
          background: 'white',
          padding: 32,
          borderRadius: 16,
          boxShadow: '0 4px 6px -1px rgb(0 0 0 / 0.1), 0 2px 4px -2px rgb(0 0 0 / 0.1)'
        }}>
          <div style={{ 
            display: 'flex',
            flexDirection: 'column',
            alignItems: 'center',
            padding: '32px',
            background: '#fef2f2',
            borderRadius: 12,
